import React, { useState } from "react";
import styled from "styled-components";
import { useSelector, useDispatch } from "react-redux";
import { NavLink, useParams } from "react-router-dom";
import AddToCart from "../assets/AddToCart.png";
import classes from "./Product.module.css";

const Product = ({ item }) => {
  const dispatch = useDispatch();

  const sendItem = () => {
    console.log(item);
    dispatch({ type: "add", obj: item });
  };

  const sendiTem2 = () => {
    dispatch({ type: "remove", obj: item });
  };

  // const decrementHandler = () => {
  //   dispatch({ type: "decrement" });
  // };

  // const toggleCounterHandler = () => {
  //   dispatch({ type: "toggle" });
  // };

  let [showCart, setShowCart] = useState(false);

  const showFn = () => {
    setShowCart(!showCart);
  };

  const kappa = useSelector((state) => state.currency);

  return (
    <div
      className={classes.container}
      onMouseEnter={showFn}
      onMouseLeave={showFn}
    >
      <div className={classes.column}>
        <div className={classes.card}>
          <img
            src={item.gallery[0]}
            className={classes.product__image}
            alt="Image not found"
          />
          <p className={classes.item__name}>{item.name}</p>
          <p>Some text</p>
        </div>
      </div>
    </div>
  );
};

export default Product;
<div className={classes.container} onMouseEnter={showFn} onMouseLeave={showFn}>
  {showCart && (
    <div className={classes.crtBack} onClick={sendItem}>
      <img className={classes.cartImg} src={AddToCart} />
    </div>
  )}
  <NavLink to={`/product/${item.id}`}>
    <img className={classes.image} src={item.gallery[0]} />
    <div className={classes.info}>
      {!item.inStock && (
        <div className={classes.outOfStockTxt}>OUT OF STOCK</div>
      )}
      <div className={classes.name}>{item.name}</div>
      <div className={classes.price}>
        {item.prices[kappa].currency.symbol} {item.prices[kappa].amount}
      </div>
    </div>
  </NavLink>
</div>;
