import { Component } from "react";
import { connect } from "react-redux";
import classes from "./CurrenctySwitcher.module.css";

const currencyQuery = `{
    currencies {
      label
      symbol
    }
  }`;

const [currency, setCurrency] = useState([]);

componentDidMount(() => {
  fetch("http://localhost:4000/", {
    body: JSON.stringify({ query: currencyQuery }),
    headers: {
      Accept: "application/json",
      "Content-Type": "application/json",
      Dnt: "1",
    },
    method: "POST",
  })
    .then((response) => response.json())
    .then((data) => {
      setCurrency(data.data.currencies);
    });
}, []);

class CurrencySwitcher1 extends Component {
  render() {
    return (
      <div className={classes.container}>
        {this.state.currency.map((curr, i) => (
          <div
            onClick={() => this.switchCurr(i)}
            className={`${classes.currencies} ${
              this.currency === i ? classes.active : undefined
            } `}
            key={curr.label}
          >
            {curr.label} {curr.symbol}
          </div>
        ))}
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    currency: state.currency,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    switchCurr: (val) => dispatch({ type: "switchCurr", curr: val }),
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(CurrencySwitcher1);
