import React from "react";
import styled from "styled-components";
import { useSelector, useDispatch } from "react-redux";
import Cart from "../assets/Cart.png";

const Container = styled.div`
  flex: 1;
  margin: 5px;
  width: 500px;
  margin: 70px 100px;
  justify-content: center;
  text-align: center;
  padding: 20px;
  &:hover {
    box-shadow: 0px 4px 35px 0px #a8acb030;
  }
`;

const Image = styled.img`
  width: 356px;
  height: 338px;
`;
const Info = styled.div`
  margin-top: 35px;
  text-align: start;
`;
const Name = styled.div`
  font-family: "Raleway", sans-serif;
  font-weight: 300;
  font-size: 18px;
  line-height: 28.8px;
  color: #1d1f22;
`;
const Price = styled.div`
  font-family: "Raleway", sans-serif;
  font-weight: 500;
  font-size: 18px;
  color: #1d1f22;
`;

const Product = ({ item }) => {
  const dispatch = useDispatch();
  const counter = useSelector((state) => state.counter);
  const itemsArr = useSelector((state) => state.itemsArr);

  const sendiTem = () => {
    dispatch({ type: "add", obj: item });
    console.log("prodcut", item);
  };

  const sendiTem2 = () => {
    dispatch({ type: "remove", obj: item });
    console.log("prodcut", item);
  };

  // const decrementHandler = () => {
  //   dispatch({ type: "decrement" });
  // };

  // const toggleCounterHandler = () => {
  //   dispatch({ type: "toggle" });
  // };

  return (
    <Container>
      <Image src={item.image}></Image>
      <Info>
        <Name>{item.title}</Name>
        <Price>{item.price}</Price>
        <Price>{item.id}</Price>
        <p style={{ background: "red" }}>{counter}</p>
        {typeof itemsArr[0]}
        <button onClick={sendiTem}>Add</button> <br />
        <button onClick={sendiTem2}>Remove</button> <br />
      </Info>
    </Container>
  );
};

export default Product;
