import React from "react";
import styled from "styled-components";
import classes from "./Dropdown.module.css";

const Container = styled.div`
  padding: 20px;
  width: 345px;
  height: 540px;
  background-color: grey;
`;
const Wrapper = styled.div``;

const Left = styled.div`
  height: 150px;
  float: left;
  display: flex;
  justify-content: flex-start;
  flex-direction: column;
  justify-content: space-between;
`;

const Bag = styled.div`
  margin-bottom: 25px;
`;

const SizeBtns = styled.div`
  display: flex;
`;

const Title = styled.div``;

const Smth = styled.div``;

const Price = styled.div``;

const Dropdown = () => {
  return (
    <Container>
      <Bag>My Bag, 2 Items</Bag>
      <Wrapper>
        <Left>
          <Title>Apollo</Title>
          <Smth>Running Short</Smth>
          <Price>$50</Price>
          <SizeBtns>
            <button className={classes.btn__size}>XS</button>
            <button className={classes.btn__size}>S</button>
            <button className={classes.btn__size}>M</button>
            <button className={classes.btn__size}>L</button>
          </SizeBtns>
        </Left>
      </Wrapper>
    </Container>
  );
};

export default Dropdown;
