import React, { useState } from "react";
import styled from "styled-components";
import { useSelector, useDispatch } from "react-redux";
import { NavLink, useParams } from "react-router-dom";
import AddToCart from "../assets/AddToCart.png";
import styles from "./Product.module.css";

const Container = styled.div`
  flex: 1;
  margin: 5px;
  width: 500px;
  margin: 70px 100px;
  justify-content: center;
  text-align: center;
  padding: 20px;
  &:hover {
    box-shadow: 0px 4px 35px 0px #a8acb030;
  }
`;

const Image = styled.img`
  width: 340px;
  height: 340px;
`;
const Info = styled.div`
  margin-top: 35px;
  text-align: start;
`;
const Name = styled.div`
  font-family: "Raleway", sans-serif;
  font-weight: 300;
  font-size: 18px;
  line-height: 28.8px;
  color: #1d1f22;
`;
const Price = styled.div`
  font-family: "Raleway", sans-serif;
  font-weight: bold;
  font-size: 18px;
  color: #1d1f22;
`;

const CrtBack = styled.div`
  background-color: #5ece7b;
  height: 52px;
  width: 52px;
  border-radius: 50%;
  margin-left: 330px;
  position: absolute;
  margin-top: 305px;
  cursor: pointer;
`;

const CartImg = styled.img`
  padding-top: 13px;
`;

const Product = ({ item }) => {
  const dispatch = useDispatch();

  const sendItem = () => {
    console.log(item);
    dispatch({ type: "add", obj: item });
  };

  const sendiTem2 = () => {
    dispatch({ type: "remove", obj: item });
  };

  // const decrementHandler = () => {
  //   dispatch({ type: "decrement" });
  // };

  // const toggleCounterHandler = () => {
  //   dispatch({ type: "toggle" });
  // };

  let [showCart, setShowCart] = useState(false);

  const showFn = () => {
    setShowCart(!showCart);
  };

  const kappa = useSelector((state) => state.currency);

  return (
    <div
      className={styles.container}
      onMouseEnter={showFn}
      onMouseLeave={showFn}
    >
      {showCart && (
        <div className={styles.crtBack} onClick={sendItem}>
          <img className={styles.crt} src={AddToCart} />
        </div>
      )}
      <NavLink to={`/product/${item.id}`}>
        <Image src={item.gallery[0]}></Image>
        <div className={styles.info}>
          {!item.inStock && (
            <div className={styles.outOfStockTxt}>OUT OF STOCK</div>
          )}
          <Name>{item.name}</Name>
          <div className={styles.price}>
            {item.prices[kappa].currency.symbol} {item.prices[kappa].amount}
          </div>
        </div>
      </NavLink>
    </div>
  );
};

export default Product;
