import React, { useEffect, useState } from "react";
import styles from "./CurrenctySwitcher.module.css";
import { useSelector, useDispatch } from "react-redux";

const CurrencySwitcher = () => {
  const currencyQuery = `{
    currencies {
      label
      symbol
    }
  }`;

  let curr = 0;

  const [currency, setCurrency] = useState([]);
  useEffect(() => {
    fetch("http://localhost:4000/", {
      body: JSON.stringify({ query: currencyQuery }),
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
        Dnt: "1",
      },
      method: "POST",
    })
      .then((response) => response.json())
      .then((data) => {
        setCurrency(data.data.currencies);
      });
  }, []);

  const dispatch = useDispatch();

  const currencyState = useSelector((state) => state.currency);

  const switchCurr = (val) => {
    dispatch({ type: "switchCurr", curr: val });
  };

  if (currency.length === 0) {
    return null;
  }
  return (
    <div className={styles.container}>
      {currency.map((curr, i) => (
        <div
          onClick={() => switchCurr(i)}
          className={`${styles.currencies} ${
            currencyState === i ? styles.active : undefined
          } `}
          key={curr.label}
        >
          {curr.label} {curr.symbol}
        </div>
      ))}
    </div>
  );
};

export default CurrencySwitcher;
