import React from "react";
import classes from "./CartDropdown.module.css";
import { useSelector, useDispatch } from "react-redux";
import { NavLink } from "react-router-dom";

const CartDropdown = () => {
  const itemsArr = useSelector((state) => state.itemsArr);

  const currencyState = useSelector((state) => state.currency);

  return (
    <div className={classes.container}>
      <div className={classes.bag}>
        My Bag,
        <span className={classes.items}>
          {itemsArr.length === 1
            ? itemsArr.length + " Item"
            : itemsArr.length + " Items"}
        </span>
      </div>
      {itemsArr.map((item, i) => (
        <div className={classes.wrapper} key={i}>
          <div className={classes.left__menu}>
            <div className={classes.item__name}>{item.brand}</div>
            <div className={classes.smth}>{item.name}</div>
            <div className={classes.item__price}>
              {item.prices[0].currency.symbol}
              {item.prices[0].amount}
            </div>
            <div className={classes.size__buttons}>
              <button className={classes.btn__size}>XS</button>
              <button className={classes.btn__size}>S</button>
              <button className={classes.btn__size}>M</button>
              <button className={classes.btn__size}>L</button>
            </div>
          </div>
          <div className={classes.right__menu}>
            <div className={classes.quantiti__buttons}>
              <button type="submit" className={classes.btn__plus__minus}>
                +
              </button>
              <div className={classes.quantity}>1</div>
              <button type="submit" className={classes.btn__plus__minus}>
                -
              </button>
            </div>
            <img className={classes.cart__img} src={item.gallery[0]} />
          </div>
        </div>
      ))}
      <div className={classes.total__container}>
        <div className={classes.totalTxt}>Total</div>
        <div className={classes.totalAmount}>$100.00</div>
      </div>
      <NavLink to="/mycart" className={classes.view__cart}>
        VIEW CART
      </NavLink>
      <p className={classes.checkout}>CHECK OUT</p>
    </div>
  );
};

export default CartDropdown;
