import { Component } from "react";
import { connect } from "react-redux";
import classes from "./SingleProduct.module.css";

class SingleProduct1 extends Component {
  constructor(props) {
    super(props);
    this.state = {
      currencyState: [],
      singleItem: [],
    };
    itemDescription = this.singleItem.description;
    console.log(itemDescription);
  }

  itemQuerry = `
  {
    product(id: "${this.id}"){
      id
      name
      inStock
      description
      category
      brand
      gallery
      attributes{
        id
        name
        type
      }
      prices{
        currency{
          label
          symbol
        }
        amount
      }
    }
  }
  `;

  componentDidMount() {
    fetch("http://localhost:4000/", {
      body: JSON.stringify({ query: this.itemQuerry }),
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
        Dnt: "1",
      },
      method: "POST",
    })
      .then((response) => response.json())
      .then((data) => {
        this.setState({ singleItem: data.data.product });
      });
  }

  render() {
    return (
      <div className={classes.container}>
        <div className={classes.left__container}>
          {this.singleItem.gallery.map((item, i) => (
            <img className={classes.left__image} src={item} key={i} />
          ))}
        </div>

        <div className={classes.center__image__container}>
          <img
            className={classes.center__image}
            src={this.singleItem.gallery[0]}
          />
        </div>
        <div className={classes.infoContainer}>
          <div className={classes.item__brand}>{this.singleItem.brand}</div>
          <div className={classes.item__name}>{this.singleItem.name}</div>
          <div className={classes.item__size}>SIZE:</div>

          <div className={classes.size__buttons}>
            {/* {singleItem.attributes.size.map((item1) => (
              // <button className={classes.btn__size}>{item1}</button>
            ))} */}
          </div>

          <div className={classes.item__priceTxt}>PRICE:</div>
          <div className={classes.item__price}>
            {this.singleItem.prices[this.props.currencyState].currency.symbol}
            {this.singleItem.prices[this.props.currencyState].amount}
          </div>
          {this.singleItem.inStock && (
            <button
              className={classes.cart__button}
              onClick={this.props.addToCart}
            >
              ADD TO CART
            </button>
          )}

          {!this.singleItem.inStock && (
            <button
              className={`${classes.cart__button} ${
                !this.singleItem.inStock && classes.cart__numb
              }`}
            >
              OUT OF STOCK
            </button>
          )}
          <div
            className={classes.description}
            dangerouslySetInnerHTML={{ __html: this.itemDescription }}
          />
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    currency: state.currency,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    addToCart: (val) => dispatch({ type: "add", obj: val }),
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(SingleProduct1);
