import React, { useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import { NavLink, useParams } from "react-router-dom";
import AddToCart from "../assets/AddToCart.png";
import classes from "./Product.module.css";

class Product1 extends React.Component {
  constructor(item) {
    super(item);
    this.state = {
      showCart: false,
      currencyState: 0,
    };

    this.showFn = this.showFn.bind(this);
  }

  showFn = () => {
    this.setState({ showCart: !this.state.showCart });
  };

  render() {
    return (
      //   <div>
      //     <p>Hello From GFG</p>

      //     <button onClick={this.myFunction}>Click me!</button>
      //   </div>

      <div className={classes.container}>
        <div
          className={`${classes.column} ${
            !this.item.inStock && classes.outOfStock
          } `}
        >
          <div
            className={classes.card}
            onMouseEnter={this.showFn}
            onMouseLeave={this.showFn}
          >
            {this.showCart && this.item.inStock && (
              <div className={classes.cart__container}>
                <img className={classes.cart} src={AddToCart} />
              </div>
            )}
            <NavLink to={`/product/${this.item.id}`}>
              {!this.item.inStock && (
                <p className={classes.outOfStockText}>OUT OF STOCK</p>
              )}
              <img
                src={this.item.gallery[0]}
                className={classes.product__image}
                alt="Image not found"
              />
              <p className={classes.item__name}>
                {this.item.brand} {this.item.name}
              </p>
              <p className={classes.item__price}>
                {this.item.prices[this.currencyState].currency.symbol}
                {this.item.prices[this.currencyState].amount}
              </p>
            </NavLink>
          </div>
        </div>
      </div>
    );
  }
}

export default Product1;
