import React from "react";
import styled from "styled-components";
import { arr } from "../Data";
import Product from "./Product";
import { useEffect, useState } from "react";

const Container = styled.div`
  display: flex;
  flex-wrap: wrap;
  justify-content: space-between;
`;

const productsQuerry = `
{
  categories {
    name
    products {
      name
      id
      inStock
      gallery
      description
      category
      attributes {
        id
        name
        type
      }
      prices {
        currency {
          label
        }
        amount
      }
      brand
    }
  }
}
`;
// curl 'http://localhost:4000/' -H 'Accept-Encoding: gzip, deflate, br' -H 'Content-Type: application/json' -H 'Accept: application/json' -H 'Connection: keep-alive' -H 'DNT: 1' -H 'Origin: http://localhost:4000' --data-binary '{"query":"# Write your query or mutation here\n{\n  categories {\n    name\n    products {\n      name\n      id\n      inStock\n      gallery\n      description\n      category\n      attributes {\n        id\n        name\n        type\n      }\n      prices {\n        currency {\n          label\n        }\n        amount\n      }\n      brand\n    }\n  }\n}\n"}' --compressed
const Products = () => {
  const [productList, setProductList] = useState([]);
  useEffect(() => {
    fetch("http://localhost:4000/", {
      body: JSON.stringify({ query: productsQuerry }),
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
        Dnt: "1",
      },
      method: "POST",
    })
      .then((response) => response.json())
      .then((data) => {
        console.log(1, data);
        setProductList(data);
      });
  }, []);

  return (
    <div>
      <Container>
        {productList.map((item) => (
          <Product item={item} key={item.id} />
        ))}
      </Container>
    </div>
  );
};

export default Products;
