import React, { useState } from "react";
import { useSelector, useDispatch, connect } from "react-redux";
import { NavLink, useParams } from "react-router-dom";
import AddToCart from "../assets/AddToCart.png";
import classes from "./Product.module.css";

class Product1 extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      showCart: false,
      currencyState: 0,
    };

    this.showFn = this.showFn.bind(this);
  }

  showFn = () => {
    this.setState({ showCart: !this.state.showCart });
  };

  sendItem = () => {
    this.props.increment();
  };

  render() {
    return (
      //   <div>
      //     <p>Hello From GFG</p>

      //     <button onClick={this.myFunction}>Click me!</button>
      //   </div>

      <div className={classes.container}>
        <div
          className={`${classes.column} ${
            !this.props.item.inStock && classes.outOfStock
          } `}
        >
          <div
            className={classes.card}
            onMouseEnter={this.showFn.bind(this)}
            onMouseLeave={this.showFn.bind(this)}
          >
            {this.state.showCart && this.props.item.inStock && (
              <div
                className={classes.cart__container}
                onClick={this.sendItem.bind(this)}
              >
                <img className={classes.cart} src={AddToCart} />
              </div>
            )}
            <NavLink to={`/product/${this.props.item.id}`}>
              {!this.props.item.inStock && (
                <p className={classes.outOfStockText}>OUT OF STOCK</p>
              )}
              <img
                src={this.props.item.gallery[0]}
                className={classes.product__image}
                alt="Image not found"
              />
              <p className={classes.item__name}>
                {this.props.item.brand} {this.props.item.name}
              </p>
              <p className={classes.item__price}>
                {
                  this.props.item.prices[this.state.currencyState].currency
                    .symbol
                }
                {this.props.item.prices[this.state.currencyState].amount}
              </p>
            </NavLink>
          </div>
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    counter: state.counter,
    filter: state.filter,
    itemsArr: state.itemsArr,
    currency: state.currency,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    increment: () => dispatch({ type: "add", obj: this.item }),
  };
};
export default connect(mapStateToProps, mapDispatchToProps)(Product1);
