import React from "react";
import Navbar from "../components/Navbar/Navbar";
import styled from "styled-components";
import classes from "./Cart.module.css";

const Container = styled.div`
  background-color: #ffffff;
`;

const CartTxt = styled.div`
  font-weight: 700;
  font-size: 32px;
  line-height: 40px;
  margin-left: 120px;
  margin-top: 50px;
  margin-bottom: 50px;
`;

const Wrapper = styled.div`
  margin-right: 100px;
  margin-left: 100px;
  width: 67%;
  padding: 20px;
  border-top: 1px solid #e5e5e5;
  border-bottom: 1px solid #e5e5e5;
`;

const Image = styled.img`
  width: 141px;
  height: 185px;
`;

const Smth = styled.div`
  font-weight: 400;
  font-size: 30px;
  line-height: 27px;
`;

const Quantity = styled.div`
  text-align: center;
  font-weight: 500;
  font-size: 24px;
  line-height: 160%;
`;

const Title = styled.div`
  font-weight: 600;
  font-size: 30px;
  line-height: 27px;
`;

const Price = styled.div`
  font-style: normal;
  font-weight: bold;
  font-size: 24px;
  line-height: 18px;
`;

const Buttons = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: space-between;
`;

const Left = styled.div`
  height: 185px;
  float: left;
  display: flex;
  justify-content: flex-start;
  flex-direction: column;
  justify-content: space-between;
`;

const Right = styled.div`
  display: flex;
  justify-content: flex-end;
`;

const SizeBtns = styled.div`
  display: flex;
`;

function Cart() {
  return (
    <Container>
      <Navbar />
      <CartTxt>CART</CartTxt>
      <Wrapper>
        <Left>
          <Title>Apollo</Title>
          <Smth>Apollo</Smth>
          <Price>$50</Price>
          <SizeBtns>
            <button className={classes.btn__size}>XS</button>
            <button className={classes.btn__size}>S</button>
            <button className={classes.btn__size}>M</button>
            <button className={classes.btn__size}>L</button>
          </SizeBtns>
        </Left>
        <Right>
          <Buttons>
            <button type="submit" className={classes.btn__plus}>
              +
            </button>
            <Quantity>1</Quantity>
            <button type="submit" className={classes.btn__plus}>
              -
            </button>
          </Buttons>
          <Image src="https://fakestoreapi.com/img/81fPKd-2AYL._AC_SL1500_.jpg" />
        </Right>
      </Wrapper>

      <Wrapper>
        <Left>
          <Title>Apollo</Title>
          <Smth>Apollo</Smth>
          <Price>$50</Price>
          <SizeBtns>
            <button className={classes.btn__size}>XS</button>
            <button className={classes.btn__size}>S</button>
            <button className={classes.btn__size}>M</button>
            <button className={classes.btn__size}>L</button>
          </SizeBtns>
        </Left>
        <Right>
          <Buttons>
            <button type="submit" className={classes.btn__plus}>
              +
            </button>
            <Quantity>1</Quantity>
            <button type="submit" className={classes.btn__plus}>
              -
            </button>
          </Buttons>
          <Image src="https://fakestoreapi.com/img/81fPKd-2AYL._AC_SL1500_.jpg" />
        </Right>
      </Wrapper>
    </Container>
  );
}
export default Cart;
