import React from "react";
import styled from "styled-components";
import styles from "./CartDropdown.module.css";
import { useSelector, useDispatch } from "react-redux";
import { NavLink } from "react-router-dom";

const Dropdown = () => {
  const itemsArr = useSelector((state) => state.itemsArr);

  return (
    <div className={styles.container}>
      <div className={styles.bag}>
        My Bag,
        <span className={styles.items}>
          {itemsArr.length === 1
            ? itemsArr.length + " Item"
            : itemsArr.length + " Items"}
        </span>
      </div>
      {itemsArr.map((item) => (
        <div className={styles.wrapper} key={item.id}>
          <div className={styles.left__menu}>
            <div className={styles.item__name}>{item.brand}</div>
            <div className={styles.smth}>{item.name}</div>
            <div className={styles.item__price}>
              {item.prices[0].currency.symbol}
              {item.prices[0].amount}
            </div>
            <div className={styles.size__buttons}>
              <button className={styles.btn__size}>XS</button>
              <button className={styles.btn__size}>S</button>
              <button className={styles.btn__size}>M</button>
              <button className={styles.btn__size}>L</button>
            </div>
          </div>
          <div className={styles.right__menu}>
            <div className={styles.quantiti__buttons}>
              <button type="submit" className={styles.btn__plus__minus}>
                +
              </button>
              <div className={styles.quantity}>1</div>
              <button type="submit" className={styles.btn__plus__minus}>
                -
              </button>
            </div>
            <img className={styles.cart__img} src={item.gallery[0]} />
          </div>
        </div>
      ))}
      <div className={styles.total}>
        <div className={styles.totalTxt}>Total</div>
        <div className={styles.totalAmount}>$100.00</div>
      </div>
      <NavLink to="/mycart" className={styles.goTo}>
        VIEW CART
      </NavLink>
      <p className={styles.add_checkout}>CHECK OUT</p>
    </div>
  );
};

export default Dropdown;
