import { Component } from "react";
import { connect } from "react-redux";
import classes from "./SingleProduct.module.css";

class SingleProduct1 extends Component {
  constructor(props) {
    super(props);
    this.state = {
      currencyState: [],
    };
  }

  itemQuerry = `
  {
    product(id: "${id}"){
      id
      name
      inStock
      description
      category
      brand
      gallery
      attributes{
        id
        name
        type
      }
      prices{
        currency{
          label
          symbol
        }
        amount
      }
    }
  }
  `;

  componentDidMount() {
    fetch("http://localhost:4000/", {
      body: JSON.stringify({ query: this.itemQuerry }),
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
        Dnt: "1",
      },
      method: "POST",
    })
      .then((response) => response.json())
      .then((data) => {
        this.setState(data.data.product);
      });
  }

  render() {
    return (
      <div className={classes.container}>
        <div className={classes.left__container}>
          {singleItem.gallery.map((item, i) => (
            <img className={classes.left__image} src={item} key={i} />
          ))}
        </div>

        <div className={classes.center__image__container}>
          <img className={classes.center__image} src={singleItem.gallery[0]} />
        </div>
        <div className={classes.infoContainer}>
          <div className={classes.item__brand}>{singleItem.brand}</div>
          <div className={classes.item__name}>{singleItem.name}</div>
          <div className={classes.item__size}>SIZE:</div>

          <div className={classes.size__buttons}>
            {/* {singleItem.attributes.size.map((item1) => (
              // <button className={classes.btn__size}>{item1}</button>
            ))} */}
          </div>

          <div className={classes.item__priceTxt}>PRICE:</div>
          <div className={classes.item__price}>
            {singleItem.prices[currencyState].currency.symbol}
            {singleItem.prices[currencyState].amount}
          </div>
          {singleItem.inStock && (
            <button className={classes.cart__button} onClick={addToCart}>
              ADD TO CART
            </button>
          )}

          {!singleItem.inStock && (
            <button
              className={`${classes.cart__button} ${
                !singleItem.inStock && classes.cart__numb
              }`}
            >
              OUT OF STOCK
            </button>
          )}
          <div
            className={classes.description}
            dangerouslySetInnerHTML={{ __html: itemDescription }}
          />
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    currency: state.currency,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    switchCurr: (val) => dispatch({ type: "switchCurr", curr: val }),
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(SingleProduct1);
