import { createStore } from "redux";

const initialState = {
  filter: 0,
  itemsArr: [],
  currency: 0,
};
console.log(currency);

const Store = (state = initialState, action) => {
  if (action.type === "add") {
    return {
      ...state,
      itemsArr: [action.obj, ...state.itemsArr],
    };
  }
  if (action.type === "set") {
    return {
      itemsArr: state.itemsArr,
      filter: (state.filter = 0),
    };
  }
  if (action.type === "set2") {
    return {
      itemsArr: state.itemsArr,
      filter: (state.filter = 1),
    };
  }
  if (action.type === "set3") {
    return {
      itemsArr: state.itemsArr,
      filter: (state.filter = 2),
    };
  }

  if (action.type === "switchCurr") {
    return {
      itemsArr: state.itemsArr,
      currency: (state.currency = 1),
    };
  }

  // case APPEND_TODO:
  //  return { // returning a copy of orignal state
  //   ...state, //copying the original state
  //   todos: [...state.todos, action.payload] //new todos array
  //  }

  if (action.type === "remove") {
    console.log(action.obj);

    return {
      counter: state.counter - 1,
      itemsArr: state.itemsArr.push(action.obj),
    };
  }

  return state;
};

const store = createStore(Store);

export default store;
