import { Component } from "react";
import { connect } from "react-redux";
import { useParams } from "react-router-dom";

import classes from "./SingleProduct.module.css";

class SingleProduct1 extends Component {
  constructor(props) {
    super(props);
    this.state = {
      currencyState: [],
      singleItem: [],
      id: this.props.params,
    };
  }
  itemQuerry = `
  {
    product(id: "${this.props.params.id}"){
      id
      name
      inStock
      description
      category
      brand
      gallery
      attributes{
        id
        name
        type
      }
      prices{
        currency{
          label
          symbol
        }
        amount
      }
    }
  }
  `;

  //   componentDidMount() {
  //     fetch("http://localhost:4000/", {
  //       body: JSON.stringify({ query: this.itemQuerry }),
  //       headers: {
  //         Accept: "application/json",
  //         "Content-Type": "application/json",
  //         Dnt: "1",
  //       },
  //       method: "POST",
  //     })
  //       .then((response) => response.json())
  //       .then((data) => {
  //         this.setState({ singleItem: data.data.product });
  //         console.log(1, this.state.singleItem);
  //         console.log(2, this.props.params.id);
  //       });
  //   }

  componentDidUpdate(prevState) {
    if (prevState.singleItem !== this.state.singleItem) {
      fetch("http://localhost:4000/", {
        body: JSON.stringify({ query: this.itemQuerry }),
        headers: {
          Accept: "application/json",
          "Content-Type": "application/json",
          Dnt: "1",
        },
        method: "POST",
      })
        .then((response) => response.json())
        .then((data) => {
          this.setState({ singleItem: data.data.product });
          console.log(1, this.state.singleItem);
          console.log(2, this.props.params.id);
        });
    }
  }

  render() {
    return (
      <div className={classes.container}>
        <div className={classes.left__container}>
          {this.state.singleItem.gallery &&
            this.state.singleItem.gallery.map((item, i) => (
              <img className={classes.left__image} src={item} key={i} />
            ))}
        </div>

        <div className={classes.center__image__container}>
          {this.state.singleItem.gallery && (
            <img
              className={classes.center__image}
              src={this.state.singleItem.gallery[0]}
            />
          )}
        </div>
        <div className={classes.infoContainer}>
          <div className={classes.item__brand}>
            {this.state.singleItem.brand}
          </div>
          <div className={classes.item__name}>{this.state.singleItem.name}</div>
          <div className={classes.item__size}>SIZE:</div>

          <div className={classes.size__buttons}>
            {/* {this.state.singleItem.attributes.size.map((item1) => (
              <button className={classes.btn__size}>{item1}</button>
            ))} */}
          </div>

          <div className={classes.item__priceTxt}>PRICE:</div>

          {this.state.singleItem.prices && (
            <div className={classes.item__price}>
              {
                this.state.singleItem.prices[this.props.currencyState].currency
                  .symbol
              }
              {this.state.singleItem.prices[this.props.currencyState].amount}
            </div>
          )}

          {this.state.singleItem.inStock && (
            <button
              className={classes.cart__button}
              onClick={this.props.addToCart}
            >
              ADD TO CART
            </button>
          )}

          {!this.state.singleItem.inStock && (
            <button
              className={`${classes.cart__button} ${
                !this.state.singleItem.inStock && classes.cart__numb
              }`}
            >
              OUT OF STOCK
            </button>
          )}
          <div
            className={classes.description}
            // dangerouslySetInnerHTML={{ __html: this.itemDescription }}
          />
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    currency: state.currency,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    addToCart: (val) => dispatch({ type: "add", obj: val }),
  };
};

function withParams(Component) {
  return (props) => <Component {...props} params={useParams()} />;
}

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(withParams(SingleProduct1));
