import { Component } from "react";
import { connect } from "react-redux";
import { useParams } from "react-router-dom";

import classes from "./SingleProduct.module.css";

class SingleProduct extends Component {
  constructor(props) {
    super(props);
    this.state = {
      singleItem: [],
      id: this.props.params,
      attributes: [],
      color: "#44FF03",
      imageId: 0,
    };
  }

  itemQuerry = `
  {
    product(id: "${this.props.params.id}"){
      id
      name
      inStock
      description
      category
      brand
      gallery
      attributes {
        id
        name
        type
        items {
          displayValue
          value
          id
        }
      }
      prices{
        currency{
          label
          symbol
        }
        amount
      }
    }
  }
  `;

  componentDidMount() {
    fetch("http://localhost:4000/", {
      body: JSON.stringify({ query: this.itemQuerry }),
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
        Dnt: "1",
      },
      method: "POST",
    })
      .then((response) => response.json())
      .then((data) => {
        console.log(data);
        this.setState({ singleItem: data.data.product });
      });
  }

  changeColor = (e) => {
    this.setState({ color: e.target.value });
    console.log(e.target.value);
  };

  addItem = (val) => {
    let temp = val;
    temp.checked = this.state.attributes;
    this.props.sendItem(temp);
  };

  handleSubmit = (event) => {
    event.preventDefault();

    let dataArr = [];
    // console.log(event.target[0].name);

    for (let index = 0; index < event.target.length; index++) {
      dataArr.push({ [event.target[index].name]: event.target[index].value });
    }

    this.setState({ attributes: dataArr });

    console.log(this.state.attributes);
    // this.addItem(this.props.item)
  };

  handleImage = (event) => {
    this.setState({ imageId: event });
  };

  render() {
    {
      this.state.singleItem.length === 0 && null;
    }

    const { imageId } = this.state;
    const { singleItem } = this.state;

    return (
      <div className={classes.container}>
        <div className={classes.left__container}>
          {singleItem.gallery.map((item, i) => (
            <img
              className={classes.left__image}
              src={item}
              alt="not found"
              key={i}
              onClick={() => this.handleImage(i)}
            />
          ))}
        </div>

        <div className={classes.center__image__container}>
          {
            <img
              alt="not found"
              className={classes.center__image}
              src={singleItem.gallery[imageId]}
            />
          }
        </div>
        <div className={classes.infoContainer}>
          <div className={classes.item__brand}>{singleItem.brand}</div>
          <div className={classes.item__name}>{singleItem.name}</div>

          <form
            className={classes.attributeSelector__container}
            onSubmit={this.handleSubmit}
            style={{
              opacity: !singleItem.inStock && "0.5",
              pointerEvents: !singleItem.inStock && "none",
            }}
          >
            {singleItem.attributes.map((item, i) => (
              <label className={classes.attributeSelector} key={item.id}>
                <p>{item.name}</p>
                <select
                  value={item.value}
                  onChange={this.changeColor}
                  style={{
                    backgroundColor: item.type === "swatch" && this.state.color,
                    color: item.type === "swatch" && this.state.color,
                    borderRadius: "10px",
                  }}
                  name={item.name}
                  id={item.id}
                >
                  {item.items.map((e) => (
                    <option
                      style={{
                        backgroundColor: e.value,
                        color: e.value,
                      }}
                      value={e.value}
                      key={e.id}
                    >
                      {e.id}
                    </option>
                  ))}
                </select>
              </label>
            ))}
            {singleItem.attributes.length > 0 && singleItem.inStock && (
              <button className={classes.submit__button} type="submit">
                SUBMIT
              </button>
            )}
          </form>

          <div className={classes.item__priceTxt}>PRICE:</div>
          {
            <div className={classes.item__price}>
              {singleItem.prices[this.props.currency].currency.symbol}
              {singleItem.prices[this.props.currency].amount}
            </div>
          }

          {singleItem.inStock && this.state.attributes.length > 1 && (
            <button
              className={classes.cart__button}
              onClick={() => this.addItem(singleItem)}
            >
              ADD TO CART
            </button>
          )}

          {singleItem.inStock && this.state.attributes.length === 0 && (
            <button
              className={`${classes.cart__button} ${
                singleItem.inStock && classes.cart__numb
              }`}
            >
              SUBMIT ATTRIBUTES
            </button>
          )}

          {!singleItem.inStock && (
            <button
              className={`${classes.cart__button} ${
                !singleItem.inStock && classes.cart__numb
              }`}
            >
              OUT OF STOCK
            </button>
          )}

          <div
            className={classes.description}
            dangerouslySetInnerHTML={{
              __html: singleItem.description,
            }}
          />
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    currency: state.currency,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    sendItem: (val) => dispatch({ type: "add", obj: val }),
  };
};

function withParams(Component) {
  return (props) => <Component {...props} params={useParams()} />;
}

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(withParams(SingleProduct));
