import React, { useEffect, useState } from "react";
import styles from "./CurrenctySwitcher.module.css";
import { useSelector, useDispatch } from "react-redux";

const CurrencySwitcher = () => {
  const currencyQuery = `{
    currencies {
      label
      symbol
    }
  }`;

  let curr = 0;

  const [currency, setCurrency] = useState([]);
  useEffect(() => {
    fetch("http://localhost:4000/", {
      body: JSON.stringify({ query: currencyQuery }),
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
        Dnt: "1",
      },
      method: "POST",
    })
      .then((response) => response.json())
      .then((data) => {
        setCurrency(data.data.currencies);
      });
  }, []);

  const dispatch = useDispatch();

  const kappa = useSelector((state) => state.currency);

  const switchCurr = (val) => {
    dispatch({ type: "switchCurr", curr: val });
  };

  console.log(kappa);

  if (currency.length === 0) {
    return null;
  }
  return (
    <div className={styles.container}>
      <div className={styles.currencies}>
        <div onClick={() => switchCurr(0)}>
          {currency[0].symbol} {currency[0].label}
        </div>
        <div onClick={() => switchCurr(1)}>
          {currency[1].symbol} {currency[1].label}
        </div>
        <div onClick={() => switchCurr(2)}>
          {currency[2].symbol} {currency[2].label}
        </div>
        <div onClick={() => switchCurr(3)}>
          {currency[3].symbol} {currency[3].label}
        </div>
        <div onClick={() => switchCurr(4)}>
          {currency[4].symbol} {currency[4].label}
        </div>
      </div>
    </div>
  );
};

export default CurrencySwitcher;
