import styled from "styled-components";
import Navbar from "../components/Navbar/Navbar";
import styles from "./SingleProduct.module.css";

const Container = styled.div``;

const Wrapper = styled.div`
  padding: 120px;
  display: flex;
`;

const SmallContainer = styled.div`
  flex: 10%;
  flex-direction: column;
`;

const SmlImg = styled.img`
  height: 87px;
  width: 87px;
  margin-bottom: 17px;
  display: flex;
  flex-direction: column;
`;

const ImgContainer = styled.div`
  flex: 60%;
`;
const InfoContainer = styled.div`
  flex: 30%;
  margin-left: 100px;

  word-wrap: break-word;
`;

const Image = styled.img`
  width: 100%;
  height: 560px;
`;

const Title = styled.div`
  font-family: "Raleway";
  font-style: normal;
  font-weight: 600;
  font-size: 30px;
  line-height: 27px;
  margin-bottom: 20px;
`;

const Smth = styled.div`
  font-family: "Raleway";
  font-style: normal;
  font-weight: 400;
  font-size: 30px;
  line-height: 27px;
  margin-bottom: 50px;
`;

const Size = styled.div`
  font-family: "Roboto Condensed", sans-serif;
  font-style: normal;
  font-weight: 700;
  font-size: 18px;
  line-height: 18px;
  display: flex;
  align-items: center;
  text-align: center;
`;

const Button = styled.div`
  display: flex;
  justify-content: space-between;
  margin-bottom: 50px;
`;

const Description = styled.div``;

const Price = styled.div`
  font-family: "Roboto Condensed";
  font-style: normal;
  font-weight: 700;
  font-size: 18px;
  line-height: 18px;
  margin-bottom: 20px;
`;

const PriceD = styled.div`
  font-family: "Raleway";
  font-style: normal;
  font-weight: 700;
  font-size: 24px;
  line-height: 18px;
  margin-bottom: 50px;
`;

const SingleProduct = () => {
  return (
    <Container>
      <Navbar />
      <Wrapper>
        <SmallContainer>
          <SmlImg src="https://fakestoreapi.com/img/81fPKd-2AYL._AC_SL1500_.jpg"></SmlImg>
          <SmlImg src="https://fakestoreapi.com/img/81fPKd-2AYL._AC_SL1500_.jpg"></SmlImg>
          <SmlImg src="https://fakestoreapi.com/img/81fPKd-2AYL._AC_SL1500_.jpg"></SmlImg>
        </SmallContainer>
        <ImgContainer>
          <Image src="https://fakestoreapi.com/img/81fPKd-2AYL._AC_SL1500_.jpg" />
        </ImgContainer>
        <InfoContainer>
          <Title>Apollo</Title>
          <Smth>Running Short</Smth>
          <Size>SIZE:</Size>

          <Button>
            <button className={styles.btn__size}>XS</button>
            <button className={styles.btn__size}>XS</button>
            <button className={styles.btn__size}>XS</button>
            <button className={styles.btn__size}>XS</button>
          </Button>

          <Price>PRICE:</Price>
          <PriceD>$50.00</PriceD>
          <Description>
            Your perfect pack for everyday use and walks in the forest. Stash
            your laptop (up to 15 inches) in the padded sleeve, your everyday
          </Description>
        </InfoContainer>
      </Wrapper>
    </Container>
  );
};

export default SingleProduct;
