import { Component } from "react";
import { connect } from "react-redux";
import { useParams } from "react-router-dom";

import classes from "./SingleProduct.module.css";

class SingleProduct extends Component {
  constructor(props) {
    super(props);
    this.state = {
      currencyState: [],
      singleItem: [],
      id: this.props.params,
    };
  }
  itemQuerry = `
  {
    product(id: "${this.props.params.id}"){
      id
      name
      inStock
      description
      category
      brand
      gallery
      attributes{
        id
        name
        type
      }
      prices{
        currency{
          label
          symbol
        }
        amount
      }
    }
  }
  `;

  componentDidMount() {
    fetch("http://localhost:4000/", {
      body: JSON.stringify({ query: this.itemQuerry }),
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
        Dnt: "1",
      },
      method: "POST",
    })
      .then((response) => response.json())
      .then((data) => {
        this.setState({ singleItem: data.data.product });
      });
  }

  render() {
    if (this.state.singleItem.length === 0) {
      return null;
    }
    return (
      <div className={classes.container}>
        <div className={classes.left__container}>
          {this.singleItem.gallery.map((item, i) => (
            <img
              className={classes.left__image}
              src={item}
              alt="not found"
              key={i}
            />
          ))}
        </div>

        <div className={classes.center__image__container}>
          {
            <img
              alt="not found"
              className={classes.center__image}
              src={this.state.singleItem.gallery[0]}
            />
          }
        </div>
        <div className={classes.infoContainer}>
          <div className={classes.item__brand}>
            {this.state.singleItem.brand}
          </div>
          <div className={classes.item__name}>{this.state.singleItem.name}</div>
          <div className={classes.item__size}>SIZE:</div>

          <div className={classes.size__buttons}>
            {this.state.singleItem.map(item)}
            {item.checked.map((item, i) => (
              <div key={i}>
                <p className={classes.attribute__name}>{Object.keys(item)} :</p>
                <div className={classes.attribute__option}>
                  {Object.keys(item).includes("Size") && (
                    <button className={classes.size__buttons}>
                      {Object.values(item)}
                    </button>
                  )}
                  {Object.keys(item).includes("Color") ? (
                    <div
                      className={classes.color__circle}
                      style={{
                        color: Object.values(item),
                        backgroundColor: Object.values(item),
                      }}
                    ></div>
                  ) : (
                    <p>
                      {!Object.keys(item).includes("Size") &&
                        Object.values(item)}
                    </p>
                  )}
                </div>
              </div>
            ))}
          </div>

          <div className={classes.item__priceTxt}>PRICE:</div>

          {
            <div className={classes.item__price}>
              {
                this.state.singleItem.prices[this.props.currency].currency
                  .symbol
              }
              {this.state.singleItem.prices[this.props.currency].amount}
            </div>
          }

          {this.state.singleItem.inStock && (
            <button
              className={classes.cart__button}
              onClick={() => this.props.addToCart(this.state.singleItem)}
            >
              ADD TO CART
            </button>
          )}

          {!this.state.singleItem.inStock && (
            <button
              className={`${classes.cart__button} ${
                !this.state.singleItem.inStock && classes.cart__numb
              }`}
            >
              OUT OF STOCK
            </button>
          )}
          <div
            className={classes.description}
            dangerouslySetInnerHTML={{
              __html: this.state.singleItem.description,
            }}
          />
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    currency: state.currency,
    itemsArr: state.itemsArr,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    addToCart: (val) => dispatch({ type: "add", obj: val }),
  };
};

function withParams(Component) {
  return (props) => <Component {...props} params={useParams()} />;
}

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(withParams(SingleProduct));
