import React from "react";
import Product from "./Product";
import { useEffect, useState } from "react";
import { useSelector } from "react-redux";
import classes from "./Products.module.css";

const productsQuerry = `
{
  categories {
    name
    products {
      name
      id
      inStock
      gallery
      description
      category
      attributes {
        id
        name
        type
      }
      prices {
        currency {
          label
          symbol
        }
        amount
      }
      brand
    }
  }
}
`;
// curl 'http://localhost:4000/' -H 'Accept-Encoding: gzip, deflate, br' -H 'Content-Type: application/json' -H 'Accept: application/json' -H 'Connection: keep-alive' -H 'DNT: 1' -H 'Origin: http://localhost:4000' --data-binary '{"query":"# Write your query or mutation here\n{\n  categories {\n    name\n    products {\n      name\n      id\n      inStock\n      gallery\n      description\n      category\n      attributes {\n        id\n        name\n        type\n      }\n      prices {\n        currency {\n          label\n        }\n        amount\n      }\n      brand\n    }\n  }\n}\n"}' --compressed
const Products = () => {
  const filter = useSelector((state) => state.filter);
  const [productList, setProductList] = useState([]);
  useEffect(() => {
    fetch("http://localhost:4000/", {
      body: JSON.stringify({ query: productsQuerry }),
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
        Dnt: "1",
      },
      method: "POST",
    })
      .then((response) => response.json())
      .then((data) => {
        setProductList(data.data.categories[filter].products);
      });
  }, [filter]);

  console.log(productList[1].attributes);

  if (productList.length === 0) {
    return null;
  }
  return (
    <div>
      <div className={classes.container}>
        {productList.map((item) => (
          <Product item={item} key={item.id} />
        ))}
      </div>
    </div>
  );
};

export default Products;
