import { Component } from "react";
import { connect } from "react-redux";
import { NavLink } from "react-router-dom";
import AddToCart from "../assets/AddToCart.png";
import classes from "./Product.module.css";

class Product1 extends Component {
  constructor(props) {
    super(props);
    this.state = {
      showCart: false,
      attributes: [],
    };
  }

  showFn = () => {
    this.setState({ showCart: !this.state.showCart });
  };

  addItem = (val) => {
    const temp = val;
    temp.checked = this.state.attributes;
    this.props.sendItem(temp);
  };

  handleSubmit = (event) => {
    event.preventDefault();

    const dataArr = [];

    for (let index = 0; index < event.target.length; index++) {
      dataArr.push({ [event.target[index].name]: event.target[index].value });
    }

    this.setState({ attributes: dataArr });
  };

  render() {
    return (
      <div className={classes.container}>
        <div
          className={`${classes.column} ${
            !this.props.item.inStock && classes.outOfStock
          } `}
        >
          <div
            className={classes.card}
            onMouseEnter={this.showFn}
            onMouseLeave={this.showFn}
          >
            <NavLink to={`/product/${this.props.item.id}`}>
              {!this.props.item.inStock && (
                <p className={classes.outOfStockText}>OUT OF STOCK</p>
              )}
              <img
                src={this.props.item.gallery[0]}
                className={classes.product__image}
                alt="Image not found"
              />
            </NavLink>

            <p className={classes.item__name}>
              {this.props.item.brand} {this.props.item.name}
            </p>
            <div className={classes.item__price}>
              {this.props.item.prices[this.props.currency].currency.symbol}
              {this.props.item.prices[this.props.currency].amount}
            </div>
            <div className={classes.selector__container}>
              <form
                className={classes.attribute__selector}
                onSubmit={this.handleSubmit}
              >
                {this.props.item.attributes.map((item) => (
                  <label key={item.id}>
                    <p>{item.name}</p>
                    <select
                      style={{
                        backgroundColor:
                          item.type === "swatch" ? item.items.value : "red",
                      }}
                      name={item.name}
                      id={item.id}
                    >
                      {item.items.map((e) => (
                        <option
                          style={{ backgroundColor: e.value, color: e.value }}
                          key={e.id}
                        >
                          {e.value}
                        </option>
                      ))}
                    </select>
                  </label>
                ))}

                {this.state.showCart && this.props.item.inStock && (
                  <button
                    type="submit"
                    className={classes.cart__container}
                    onClick={() => this.addItem(this.props.item)}
                  >
                    <img className={classes.cart} src={AddToCart} />
                  </button>
                )}
              </form>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    filter: state.filter,
    itemsArr: state.itemsArr,
    currency: state.currency,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    sendItem: (val) => dispatch({ type: "add", obj: val }),
  };
};
export default connect(mapStateToProps, mapDispatchToProps)(Product1);
