import React, { Component } from "react";
import "./Slider.css";

class Slider1 extends Component {
  constructor(props) {
    super(props);
    this.state = {
      current: 0,
      length: props.slides.length,
    };
  }

  nextSlide = () => {
    this.setState({
      current:
        this.state.current === this.state.length - 1
          ? 0
          : this.state.current + 1,
    });
  };

  prevSlide = () => {
    this.setState({
      current:
        this.state.current === 0
          ? this.state.length - 1
          : this.state.current - 1,
    });
  };

  render() {
    if (!Array.isArray(this.props.slides) || this.state.length <= 0) {
      return null;
    }
    return (
      <section className="slider">
        <div className="left-arrow" onClick={this.prevSlide}>
          {`<`}
        </div>
        <div className="right-arrow" onClick={this.nextSlide}>
          {`>`}
        </div>
        {this.props.slides.map((slide, index) => {
          return (
            <div
              className={
                index === this.state.current ? "slide active" : "slide"
              }
              key={index}
            >
              {index === this.state.current && (
                <img src={slide} alt="travel image" className="image" />
              )}
            </div>
          );
        })}
      </section>
    );
  }
}

export default Slider1;
