import { Component } from "react";
import classes from "./Cart.module.css";
import { useSelector, useDispatch, connect } from "react-redux";
import Slider from "../components/test/Slider";

class Cart1 extends Component {
  constructor(props) {
    super(props);
    this.state = {
      itemsArr: [],
      quantity: [],
    };
  }

  render() {
    return (
      <div className={classes.container}>
        <div className={classes.cart__text}>CART</div>
        {this.props.itemsArr.length !== 0 &&
          this.props.itemsArr.map((item) => (
            <div className={classes.wrapper} key={item.id}>
              <div className={classes.left}>
                <div className={classes.item__brand}>{item.brand}</div>
                <div className={classes.item__name}>{item.name}</div>
                <div className={classes.item__price}>
                  {item.prices[this.props.currency].currency.symbol}
                  {parseFloat(item.quantity * item.prices[0].amount).toFixed(2)}
                </div>
                <div className={classes.sizeBtns}>
                  <button className={classes.size__buttons}>XS</button>
                  <button className={classes.size__buttons}>S</button>
                  <button className={classes.size__buttons}>M</button>
                  <button className={classes.size__buttons}>L</button>
                </div>
              </div>
              <div className={classes.right}>
                <div className={classes.quantity__buttons}>
                  <button
                    type="submit"
                    className={classes.plus__minus}
                    onClick={() => this.props.addToCart(item.itemsArr)}
                  >
                    +
                  </button>
                  <div className={classes.quantity}>{item.quantity}</div>
                  <button
                    type="submit"
                    className={classes.plus__minus}
                    onClick={() => this.props.addToCart(this.state.itemsArr)}
                  >
                    -
                  </button>
                </div>
                <Slider slides={item.gallery} />
              </div>
            </div>
          ))}
        {this.props.itemsArr.length === 0 && (
          <div className={classes.cart__empty}>Your cart is empty</div>
        )}
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    itemsArr: state.itemsArr,
    currency: state.currency,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    addToCart: (val) => dispatch({ type: "add", obj: val }),
    removeItem: (val) => dispatch({ type: "remove", obj: val }),
  };
};
export default connect(mapStateToProps, mapDispatchToProps)(Cart1);
