import React from "react";
import styled from "styled-components";
import classes from "./Dropdown.module.css";
import { useSelector, useDispatch } from "react-redux";

const Container = styled.div`
  padding: 20px;
  width: 345px;
`;
const Wrapper = styled.div`
  margin-bottom: 40px;
`;

const Left = styled.div`
  height: 137px;
  float: left;
  display: flex;
  justify-content: flex-start;
  flex-direction: column;
  justify-content: space-between;
`;

const Right = styled.div`
  display: flex;
  justify-content: flex-end;
`;

const Image = styled.img`
  width: 105px;
  height: 137px;
`;

const Bag = styled.div`
  margin-bottom: 25px;
  font-weight: 700;
  font-size: 16px;
`;

const Buttons = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: space-between;
`;

const SizeBtns = styled.div`
  display: flex;
`;

const Quantity = styled.div`
  text-align: center;
  font-weight: 500;
  font-size: 16px;
  line-height: 160%;
  margin-right: 20px;
`;

const Title = styled.div`
  font-weight: 300;
  font-size: 16px;
`;

const Smth = styled.div`
  font-weight: 300;
  font-size: 16px;
`;

const Price = styled.div`
  font-weight: bold;
  font-size: 16px;
`;

const Dropdown = () => {
  const dispatch = useDispatch();
  const itemsArr = useSelector((state) => state.itemsArr);

  const sendItem = () => {
    dispatch({ type: "add", obj: item });
  };

  return (
    <Container>
      <Bag>
        My Bag,
        <span className={classes.items}>
          {itemsArr.length === 1
            ? itemsArr.length + " Item"
            : itemsArr.length + " Items"}
        </span>
      </Bag>
      {itemsArr.map((item) => (
        <Wrapper>
          <Left>
            <Title>{item.title.slice(0, 10) + "..."}</Title>
            <Smth>{item.description.slice(0, 10) + "..."}</Smth>
            <Price>${item.price}</Price>
            <SizeBtns>
              <button className={classes.btn__size}>XS</button>
              <button className={classes.btn__size}>S</button>
              <button className={classes.btn__size}>M</button>
              <button className={classes.btn__size}>L</button>
            </SizeBtns>
          </Left>
          <Right>
            <Buttons>
              <button
                type="submit"
                onClick={sendItem}
                className={classes.btn__plus}
              >
                +
              </button>
              <Quantity>1</Quantity>
              <button type="submit" className={classes.btn__plus}>
                -
              </button>
            </Buttons>
            <Image src={item.image} />
          </Right>
        </Wrapper>
      ))}
    </Container>
  );
};

export default Dropdown;
