import React from "react";
import styled from "styled-components";
import styles from "./CartDropdown.module.css";
import { useSelector, useDispatch } from "react-redux";
import { NavLink } from "react-router-dom";

const Wrapper = styled.div`
  margin-bottom: 40px;
`;

const Left = styled.div`
  height: 137px;
  float: left;
  display: flex;
  justify-content: flex-start;
  flex-direction: column;
  justify-content: space-between;
`;

const Right = styled.div`
  display: flex;
  justify-content: flex-end;
`;

const Image = styled.img`
  width: 105px;
  height: 137px;
`;

const Bag = styled.div`
  margin-bottom: 25px;
  font-weight: 700;
  font-size: 16px;
`;

const Buttons = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: space-between;
`;

const SizeBtns = styled.div`
  display: flex;
`;

const Quantity = styled.div`
  text-align: center;
  font-weight: 500;
  font-size: 16px;
  line-height: 160%;
  margin-right: 20px;
`;

const Title = styled.div`
  font-weight: 300;
  font-size: 16px;
`;

const Smth = styled.div`
  font-weight: 300;
  font-size: 16px;
`;

const Price = styled.div`
  font-weight: bold;
  font-size: 16px;
`;

const Dropdown = () => {
  const itemsArr = useSelector((state) => state.itemsArr);

  return (
    <div className={styles.container}>
      <div className={styles.bag}>
        My Bag,
        <span className={styles.items}>
          {itemsArr.length === 1
            ? itemsArr.length + " Item"
            : itemsArr.length + " Items"}
        </span>
      </div>
      {itemsArr.map((item) => (
        <div className={styles.wrapper} key={item.id}>
          <div className={styles.left}>
            <div className={styles.title}>{item.brand}</div>
            <div className={styles.smth}>{item.name}</div>
            <div className={styles.price}>
              {item.prices[0].currency.symbol}
              {item.prices[0].amount}
            </div>
            <div className={styles.sizeBtns}>
              <button className={styles.btn__size}>XS</button>
              <button className={styles.btn__size}>S</button>
              <button className={styles.btn__size}>M</button>
              <button className={styles.btn__size}>L</button>
            </div>
          </div>
          <div className={styles.right}>
            <div className={styles.buttons}>
              <button type="submit" className={styles.btn__plus}>
                +
              </button>
              <div className={styles.quantity}>1</div>
              <button type="submit" className={styles.btn__plus}>
                -
              </button>
            </div>
            <Image src={item.gallery[0]} />
          </div>
        </div>
      ))}
      <div className={styles.total}>
        <div className={styles.totalTxt}>Total</div>
        <div className={styles.totalAmount}>$100.00</div>
      </div>
      <div className={styles.buttons}>
        <NavLink to="/mycart" className={styles.goTo}>
          VIEW CART
        </NavLink>
        <button className={`${styles.goTo} ${styles.add_checkout}`}>
          CHECK OUT
        </button>
      </div>
    </div>
  );
};

export default Dropdown;
