import React, { useEffect, useState } from "react";
import styles from "./CurrenctySwitcher.module.css";
import { useSelector, useDispatch } from "react-redux";

const CurrencySwitcher = () => {
  const currencyQuery = `{
    currencies {
      label
      symbol
    }
  }`;

  const [currency, setCurrency] = useState([]);
  useEffect(() => {
    fetch("http://localhost:4000/", {
      body: JSON.stringify({ query: currencyQuery }),
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
        Dnt: "1",
      },
      method: "POST",
    })
      .then((response) => response.json())
      .then((data) => {
        setCurrency(data.data.currencies);
      });
  }, []);

  const dispatch = useDispatch();

  const kappa = useSelector((state) => state.currency);

  const switchCurr = () => {
    dispatch({ type: "switchCurr" });
  };

  console.log(kappa);

  if (currency.length === 0) {
    return null;
  }
  return (
    <div className={styles.container}>
      <div className={styles.currencies}>
        {currency.map((curr) => (
          <button onClick={switchCurr} key={curr.label}>
            {curr.symbol} {curr.label}
          </button>
        ))}
      </div>
    </div>
  );
};

export default CurrencySwitcher;
