import { Component } from "react";
import classes from "./CartDropdown.module.css";
import { connect } from "react-redux";
import { NavLink } from "react-router-dom";

class CartDropdown1 extends Component {
  componentDidUpdate(prevProps) {
    if (prevProps.itemsArr !== this.props.itemsArr) {
      this.setState({
        cartBadgeQty: this.props.itemsArr.reduce((acummulator, object) => {
          return acummulator + object.quantity;
        }, 0),
      });
    }
  }

  render() {
    return (
      <div className={classes.container}>
        <div className={classes.bag}>
          My Bag,
          <span style={{ marginLeft: "10px" }} className={classes.items}>
            {this.state.cartBadgeQty}
          </span>
        </div>
        {this.props.itemsArr.map((item, i) => (
          <div className={classes.wrapper} key={i}>
            <div className={classes.left__menu}>
              <div className={classes.item__name}>{item.brand}</div>
              <div className={classes.smth}>{item.name}</div>
              <div className={classes.item__price}>
                {item.prices[this.props.currency].currency.symbol}
                {parseFloat(
                  item.quantity * item.prices[this.props.currency].amount
                ).toFixed(2)}
              </div>
              <div className={classes.size__buttons}>
                <button className={classes.btn__size}>XS</button>
                <button className={classes.btn__size}>S</button>
                <button className={classes.btn__size}>M</button>
                <button className={classes.btn__size}>L</button>
              </div>
            </div>
            <div className={classes.right__menu}>
              <div className={classes.quantiti__buttons}>
                <button
                  type="submit"
                  className={classes.btn__plus__minus}
                  onClick={() => this.props.addToCart(item)}
                >
                  +
                </button>
                <div className={classes.quantity}>{item.quantity}</div>
                <button
                  type="submit"
                  className={classes.btn__plus__minus}
                  onClick={() => this.props.removeItem(item)}
                >
                  -
                </button>
              </div>
              <img className={classes.cart__img} src={item.gallery[0]} />
            </div>
          </div>
        ))}
        <div className={classes.total__container}>
          <div className={classes.totalTxt}>Total</div>
          <div className={classes.totalAmount}>10</div>
        </div>
        <NavLink to="/mycart" className={classes.view__cart}>
          VIEW CART
        </NavLink>
        <p className={classes.checkout}>CHECK OUT</p>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    currency: state.currency,
    itemsArr: state.itemsArr,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    addToCart: (val) => dispatch({ type: "add", obj: val }),
    removeItem: (val) => dispatch({ type: "remove", obj: val }),
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(CartDropdown1);
