import React, { useState } from "react";
import styled from "styled-components";
import { useSelector, useDispatch } from "react-redux";
import { NavLink, useParams } from "react-router-dom";
import styles from "./Product.module.css";
import AddToCart from "../assets/AddToCart.png";

const Container = styled.div`
  flex: 1;
  margin: 5px;
  width: 500px;
  margin: 70px 100px;
  justify-content: center;
  text-align: center;
  padding: 20px;
  &:hover {
    box-shadow: 0px 4px 35px 0px #a8acb030;
  }
`;

const Image = styled.img`
  width: 356px;
  height: 338px;
`;
const Info = styled.div`
  margin-top: 35px;
  text-align: start;
`;
const Name = styled.div`
  font-family: "Raleway", sans-serif;
  font-weight: 300;
  font-size: 18px;
  line-height: 28.8px;
  color: #1d1f22;
`;
const Price = styled.div`
  font-family: "Raleway", sans-serif;
  font-weight: bold;
  font-size: 18px;
  color: #1d1f22;
`;

const CrtBack = styled.div`
  background-color: #5ece7b;
  height: 52px;
  width: 52px;
  border-radius: 50%;
  margin-left: 300px;
  position: absolute;
  margin-top: 300px;
  cursor: pointer;
`;

const CartImg = styled.img`
  padding-top: 13px;
`;

const Product = ({ item }) => {
  const dispatch = useDispatch();
  const counter = useSelector((state) => state.counter);
  const itemsArr = useSelector((state) => state.itemsArr);

  let { id } = useParams();

  const sendiTem = () => {
    dispatch({ type: "add", obj: item });
  };

  const sendiTem2 = () => {
    dispatch({ type: "remove", obj: item });
  };

  // const decrementHandler = () => {
  //   dispatch({ type: "decrement" });
  // };

  // const toggleCounterHandler = () => {
  //   dispatch({ type: "toggle" });
  // };

  let [showCart, setShowCart] = useState(false);

  const showFn = () => {
    setShowCart(!showCart);
  };

  return (
    <Container onMouseEnter={showFn} onMouseLeave={showFn}>
      {showCart && (
        <CrtBack>
          <CartImg onClick={sendiTem} src={AddToCart} />
        </CrtBack>
      )}
      <NavLink to={`/product/${item.id}`}>
        <Image onClick={sendiTem} src={item.image}></Image>
        <Info>
          <Name>{item.title}</Name>
          <div className={styles.smth}>${item.price}</div>
          {/* {kek && ( */}
        </Info>
      </NavLink>
    </Container>
  );
};

export default Product;
