import React from "react";
import { Component } from "react";
import logo from "../../assets/logo.png";
import styled from "styled-components";
import sign from "../../assets/$.png";
import Cart from "../../assets/Cart.png";
import Vector from "../../assets/Vector.png";
import classes from "./Navbar.module.css";
import Dropdown from "../Dropdown/Dropdown";
import { NavLink } from "react-router-dom";
import { useSelector, useDispatch } from "react-redux";

const Container = styled.div`
  height: 80px;
  padding-left: 100px;
  padding-right: 100px;
`;

const Wrapper = styled.div`
  padding: 10px 20px;

  display: flex;
  justify-content: space-between;
  align-items: center;
`;

const Left = styled.div`
  flex: 1;
  display: flex;
  font-weight: 600;
  font-size: 16px;
`;
const Center = styled.div`
  flex: 1;
  text-align: center;
  padding-top: 10px;
`;

const Right = styled.div`
  flex: 1;
  justify-content: flex-end;
  display: flex;
  align-items: center;
`;

const Lmenu = styled.div`
  align-items: center;
  margin-left: 40px;
`;

const Rmenu = styled.div`
  align-items: center;
  margin-right: 40px;
`;

class Navbar extends Component {
  render() {
    return (
      <Container>
        <Wrapper>
          <Left>
            <a href="">ALL</a>
            <Lmenu>
              <a href="">CLOTHES</a>
            </Lmenu>
            <Lmenu>
              <a href="">TECH</a>
            </Lmenu>
          </Left>
          <Center>
            <NavLink to="">
              <img src={logo} />
            </NavLink>
          </Center>
          <Right>
            <Rmenu>
              <img src={sign} />
              <img src={Vector} />
            </Rmenu>
            <Rmenu>
              <div className={classes.dropdown}>
                <button className={classes.dropbtn}>
                  <img src={Cart} />
                </button>
                <div className={classes.dropdown_content}>
                  <Dropdown />
                  <div className={classes.total}>
                    <div className={classes.totalTxt}>Total</div>
                    <div className={classes.totalAmount}>$100.00</div>
                  </div>
                  <div className={classes.buttons}>
                    <NavLink to="/mycart" className={classes.goTo}>
                      VIEW BAG
                    </NavLink>
                    <button
                      className={`${classes.goTo} ${classes.add_checkout}`}
                    >
                      CHECK OUT
                    </button>
                  </div>
                </div>
              </div>
            </Rmenu>
          </Right>
        </Wrapper>
      </Container>
    );
  }
}

export default Navbar;
