import classes from "./SingleProduct.module.css";
import { useParams } from "react-router-dom";
import { useState, useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";

const SingleProduct = () => {
  let { id } = useParams();
  const itemQuerry = `
{
  product(id: "${id}"){
    id
    name
    inStock
    description
    category
    brand
    gallery
    attributes{
      id
      name
      type
    }
    prices{
      currency{
        label
        symbol
      }
      amount
    }
  }
}
`;

  console.log(singleItem.attributes);
  const dispatch = useDispatch();

  const addToCart = () => {
    dispatch({ type: "add", obj: singleItem });
  };

  const [singleItem, setsingleItem] = useState([]);
  useEffect(() => {
    fetch("http://localhost:4000/", {
      body: JSON.stringify({ query: itemQuerry }),
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
        Dnt: "1",
      },
      method: "POST",
    })
      .then((response) => response.json())
      .then((data) => {
        setsingleItem(data.data.product);
      });
  }, []);
  console.log(singleItem.gallery);
  if (singleItem.length === 0) {
    return null;
  }
  return (
    <div className={classes.container}>
      <div className={classes.smallContainer}>
        {singleItem.gallery.map((item) => (
          <img className={classes.smlImg} src={item} />
        ))}
      </div>
      <div className={classes.imgContainer}>
        <img className={classes.image} src={singleItem.gallery[0]} />
      </div>
      <div className={classes.infoContainer}>
        <div className={classes.title}>{singleItem.category}</div>
        <div className={classes.smth}>{singleItem.name}</div>
        <div className={classes.size}>SIZE:</div>

        <div className={classes.button}>
          <button className={classes.btn__size}>XS</button>
          <button className={classes.btn__size}>S</button>
          <button className={classes.btn__size}>M</button>
          <button className={classes.btn__size}>L</button>
        </div>

        <div className={classes.price}>PRICE:</div>
        <div className={classes.priceD}>{singleItem.prices[0].amount}$</div>
        <button className={classes.cart__button} onClick={addToCart}>
          ADD TO CART
        </button>
        <div className={classes.description}>{singleItem.description}</div>
      </div>
    </div>
  );
};

export default SingleProduct;
