import { Component } from "react";
import logo from "../../assets/logo.png";
import sign from "../../assets/$.png";
import Cart from "../../assets/Cart.png";
import Vector from "../../assets/Vector.png";
import classes from "./Navbar.module.css";
import CartDropdown from "../Dropdowns/CartDropdown";
import CurrencySwitcher from "../Dropdowns/CurrencySwitcher";
import { NavLink } from "react-router-dom";
import { connect } from "react-redux";

class Navbar1 extends Component {
  constructor(props) {
    super(props);
    this.state = {
      cartBadgeQty: 0,
    };
  }

  componentDidUpdate(prevProps, prevState) {
    if (prevProps.itemsArr !== this.props.itemsArr) {
      this.setState({
        cartBadgeQty: this.props.itemsArr.reduce((acummulator, object) => {
          return acummulator + object.quantity;
        }, 0),
      });
    }
  }

  render() {
    return (
      <div className={classes.container}>
        <div className={classes.wrapper}>
          <div className={classes.left__menu}>
            <NavLink
              to="/"
              onClick={() => this.props.setFilterState(0)}
              className={this.props.filter === 0 ? classes.active : undefined}
            >
              ALL
            </NavLink>
            <div className={classes.lmenu__categories}>
              <NavLink
                to="/"
                onClick={() => this.props.setFilterState(1)}
                className={this.props.filter === 1 ? classes.active : undefined}
              >
                CLOTHES
              </NavLink>
            </div>
            <div className={classes.lmenu__categories}>
              <NavLink
                to="/"
                onClick={() => this.props.setFilterState(2)}
                className={this.props.filter === 2 ? classes.active : undefined}
              >
                TECH
              </NavLink>
            </div>
          </div>
          <div className={classes.center__menu}>
            <NavLink to="/">
              <img src={logo} />
            </NavLink>
          </div>
          <div className={classes.right__menu}>
            <div className={classes.rmenu__categories}>
              <div className={classes.dropdown}>
                <button className={classes.dropbtn}>
                  <img src={sign} />
                  <img src={Vector} />
                </button>
                <div className={classes.dropdown_content}>
                  <CurrencySwitcher />
                </div>
              </div>
            </div>
            <div className={classes.rmenu__categories}>
              <div className={classes.dropdown}>
                <button className={classes.dropbtn}>
                  <div className={classes.cart__badge}>
                    <img src={Cart} />
                    <div className={classes.badge}>
                      {this.state.cartBadgeQty}
                    </div>
                  </div>
                </button>
                <div className={classes.dropdown_content}>
                  <CartDropdown />
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    filter: state.filter,
    itemsArr: state.itemsArr,
    currency: state.currency,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    setFilterState: (val) => dispatch({ type: "set", filterState: val }),
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(Navbar1);
