import styled from "styled-components";
import Navbar from "../components/Navbar/Navbar";
import styles from "./SingleProduct.module.css";
import { useParams } from "react-router-dom";
import { arr } from "../Data";
import { useState, useEffect } from "react";

const Container = styled.div``;

const Wrapper = styled.div`
  padding: 120px;
  display: flex;
`;

const SmallContainer = styled.div`
  flex: 10%;
  flex-direction: column;
`;

const SmlImg = styled.img`
  height: 87px;
  width: 87px;
  margin-bottom: 17px;
  display: flex;
  flex-direction: column;
`;

const ImgContainer = styled.div`
  flex: 50%;
`;
const InfoContainer = styled.div`
  flex: 30%;
  padding-left: 8%;
  padding-right: 8%;
  word-wrap: break-word;
`;

const Image = styled.img`
  width: 100%;
  height: 560px;
`;

const Title = styled.div`
  font-family: "Raleway";
  font-style: normal;
  font-weight: 600;
  font-size: 30px;
  line-height: 27px;
  margin-bottom: 20px;
`;

const Smth = styled.div`
  font-family: "Raleway";
  font-style: normal;
  font-weight: 400;
  font-size: 30px;
  line-height: 27px;
  margin-bottom: 35px;
`;

const Size = styled.div`
  font-family: "Roboto Condensed", sans-serif;
  font-style: normal;
  font-weight: 700;
  font-size: 18px;
  line-height: 18px;
  display: flex;
  align-items: center;
  text-align: center;
  margin-bottom: 5px;
`;

const Button = styled.div`
  margin-bottom: 35px;
  display: flex;
  justify-content: space-between;
`;

const Description = styled.div`
  font-family: "Roboto";
  font-style: normal;
  font-weight: 400;
  font-size: 16px;
  line-height: 159.96%;
`;

const Price = styled.div`
  font-family: "Roboto Condensed";
  font-style: normal;
  font-weight: 700;
  font-size: 18px;
  line-height: 18px;
  margin-bottom: 20px;
`;

const PriceD = styled.div`
  font-family: "Raleway";
  font-style: normal;
  font-weight: 700;
  font-size: 24px;
  line-height: 18px;
  margin-bottom: 30px;
`;

const SingleProduct = () => {
  let { id } = useParams();
  let [product, setProduct] = useState([]);
  product = arr.filter((e) => e.id == id);

  // useEffect(() => {
  //   product = arr.filter((e) => e.id == id);
  //   setProduct(product);
  // }, []);
  console.log(product);

  return (
    <Container>
      <Wrapper>
        <SmallContainer>
          <SmlImg src={product[0].image}></SmlImg>
          <SmlImg src={product[0].image}></SmlImg>
          <SmlImg src={product[0].image}></SmlImg>
        </SmallContainer>
        <ImgContainer>
          <Image src={product[0].image} />
        </ImgContainer>
        <InfoContainer>
          <Title>{product[0].category}</Title>
          <Smth>{product[0].title}</Smth>
          <Size>SIZE:</Size>

          <Button>
            <button className={styles.btn__size}>XS</button>
            <button className={styles.btn__size}>S</button>
            <button className={styles.btn__size}>M</button>
            <button className={styles.btn__size}>L</button>
          </Button>

          <Price>PRICE:</Price>
          <PriceD>{product[0].price}$</PriceD>
          <button className={styles.cart__button}>ADD TO CART</button>
          <Description>{product[0].description}</Description>
        </InfoContainer>
      </Wrapper>
    </Container>
  );
};

export default SingleProduct;
