import { createStore } from "redux";

const initialState = {
  filter: 0,
  itemsArr: [],
  currency: 0,
  quantity: 0,
};

const Store = (state = initialState, action) => {
  if (action.type === "add") {
    if (state.itemsArr.length === 0) {
      return {
        ...state,
        itemsArr: [action.obj, ...state.itemsArr],
      };
    }

    if (state.itemsArr.findIndex((x) => x.id === action.obj.id) >= 0) {
      console.log("opaa");
    } else {
      console.log("opaa2");
    }

    return {
      ...state,
      // itemsArr: [action.obj, ...state.itemsArr],

      // const numb = array1.findIndex(x => x.id === '74'),

      // array1[0].id = array1[0].id + 1,

      // console.log(array1);
      // console.log(numb);
    };
  }
  if (action.type === "clean") {
    return {
      // returning a copy of orignal state
      ...state, //copying the original state
      itemsArr: state.itemsArr.filter((e) => e.id !== action.payload),
    };
  }

  if (action.type === "set") {
    return {
      ...state,
      filter: (state.filter = action.filterState),
    };
  }

  if (action.type === "switchCurr") {
    return {
      ...state,
      currency: (state.currency = action.curr),
    };
  }

  // case APPEND_TODO:
  //  return { // returning a copy of orignal state
  //   ...state, //copying the original state
  //   todos: [...state.todos, action.payload] //new todos array
  //  }

  if (action.type === "remove") {
    return {
      counter: state.counter - 1,
      itemsArr: state.itemsArr.push(action.obj),
    };
  }

  return state;
};

const store = createStore(Store);

export default store;
