import classes from "./SingleProduct.module.css";
import { useParams } from "react-router-dom";
import { useState, useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";

const SingleProduct = () => {
  let { id } = useParams();
  const itemQuerry = `
{
  product(id: "${id}"){
    id
    name
    inStock
    description
    category
    brand
    gallery
    attributes{
      id
      name
      type
    }
    prices{
      currency{
        label
        symbol
      }
      amount
    }
  }
}
`;

  const dispatch = useDispatch();

  const addToCart = () => {
    dispatch({ type: "add", obj: singleItem });
  };

  const [singleItem, setsingleItem] = useState([]);
  useEffect(() => {
    fetch("http://localhost:4000/", {
      body: JSON.stringify({ query: itemQuerry }),
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
        Dnt: "1",
      },
      method: "POST",
    })
      .then((response) => response.json())
      .then((data) => {
        setsingleItem(data.data.product);
      });
  }, []);

  // console.log(singleItem.attributes[0].name);

  const currencyState = useSelector((state) => state.currency);
  const itemDescription = singleItem.description;
  if (singleItem.length === 0) {
    return null;
  }
  return (
    <div className={classes.container}>
      <div className={classes.left__container}>
        {singleItem.gallery.map((item, i) => (
          <img className={classes.smlImg} src={item} key={i} />
        ))}
      </div>
      <div className={classes.imgContainer}>
        <img className={classes.image} src={singleItem.gallery[0]} />
      </div>
      <div className={classes.infoContainer}>
        <div className={classes.title}>{singleItem.brand}</div>
        <div className={classes.smth}>{singleItem.name}</div>
        <div className={classes.size}>SIZE:</div>

        <div className={classes.button}>
          {/* {singleItem.attributes.size.map((item1) => (
            <button className={classes.btn__size}>{item1}</button>
          ))} */}
        </div>

        <div className={classes.price}>PRICE:</div>
        <div className={classes.priceD}>
          {singleItem.prices[currencyState].currency.symbol}
          {singleItem.prices[currencyState].amount}
        </div>
        {singleItem.inStock && (
          <button className={classes.cart__button} onClick={addToCart}>
            ADD TO CART
          </button>
        )}

        {!singleItem.inStock && (
          <button
            className={`${classes.cart__button} ${
              !singleItem.inStock && classes.cart__numb
            }`}
          >
            OUT OF STOCK
          </button>
        )}
        <div
          className={classes.description}
          dangerouslySetInnerHTML={{ __html: itemDescription }}
        />
      </div>
    </div>
  );
};

export default SingleProduct;
