import { Component } from "react";
import { connect } from "react-redux";
import { NavLink } from "react-router-dom";

import classes from "./Product.module.css";
import AddToCart from "../../assets/AddToCart.png";

class Product extends Component {
  constructor(props) {
    super(props);
    this.state = {
      showCart: false,
      attributes: [],
      color: "#44FF03",
    };
  }

  showFn = () => {
    this.setState({ showCart: !this.state.showCart });
  };

  colorChange = (e) => {
    this.setState({ color: e.target.value });
  };

  render() {
    function siri(val, event) {}
    return (
      <div className={classes.container}>
        <div
          className={`${classes.column} ${
            !this.props.item.inStock && classes.outOfStock
          } `}
        >
          <div
            className={classes.card}
            onMouseEnter={this.showFn}
            onMouseLeave={this.showFn}
          >
            <NavLink to={`/product/${this.props.item.id}`}>
              {!this.props.item.inStock && (
                <p className={classes.outOfStockText}>OUT OF STOCK</p>
              )}
              <img
                src={this.props.item.gallery[0]}
                className={classes.product__image}
                alt="not found"
              />
            </NavLink>

            <p className={classes.item__name}>
              {this.props.item.brand} {this.props.item.name}
            </p>
            <div className={classes.item__price}>
              {this.props.item.prices[this.props.currency].currency.symbol}
              {this.props.item.prices[this.props.currency].amount}
            </div>
            <div>
              <form
                style={{ pointerEvents: !this.props.item.inStock && "none" }}
                className={classes.selector__container}
              >
                {this.props.item.attributes.map((item) => (
                  <label className={classes.attribute__selector} key={item.id}>
                    <p>{item.name}</p>
                    <select
                      onChange={this.colorChange}
                      value={item.value}
                      style={{
                        backgroundColor:
                          item.type === "swatch" && this.state.color,
                        color: item.type === "swatch" && this.state.color,
                        width: item.type === "swatch" && "45px",
                        borderRadius: "10px",
                      }}
                      name={item.name}
                      id={item.id}
                    >
                      {item.items.map((e) => (
                        <option
                          style={{
                            backgroundColor: e.value,
                            color: e.value,
                          }}
                          key={e.id}
                        >
                          {item.type === "swatch" ? e.id : e.value}
                        </option>
                      ))}
                    </select>
                  </label>
                ))}

                {this.state.showCart && this.props.item.inStock && (
                  <button
                    className={classes.cart__container}
                    onClick={() => this.addItem(this.props.item)}
                    // onSubmit={this.handleSubmit}
                  >
                    <img
                      className={classes.cart}
                      src={AddToCart}
                      alt="not found"
                    />
                  </button>
                )}
              </form>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    filter: state.filter,
    itemsArr: state.itemsArr,
    currency: state.currency,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    sendItem: (val) => dispatch({ type: "add", obj: val }),
  };
};
export default connect(mapStateToProps, mapDispatchToProps)(Product);
