import { Component } from "react";
import { connect } from "react-redux";

import Product1 from "./Product1";

import classes from "./Products.module.css";

class Products1 extends Component {
  constructor(props) {
    super(props);
    this.state = {
      productList: [],
      filter: 0,
    };
  }

  productsQuerry = `
{
  categories {
    name
    products {
      name
      id
      inStock
      gallery
      description
      category
      attributes {
        id
        name
        type
        items {
          displayValue
          value
          id
        }
      }
      prices {
        currency {
          label
          symbol
        }
        amount
      }
      brand
    }
  }
}
`;

  componentDidMount() {
    fetch("http://localhost:4000/", {
      body: JSON.stringify({ query: this.productsQuerry }),
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
        Dnt: "1",
      },
      method: "POST",
    })
      .then((response) => response.json())
      .then((data) => {
        this.setState({
          productList: data.data.categories[this.props.filter].products,
          filter: 1,
        });
      });
  }

  render() {
    if (this.state.productList.length === 0) {
      return null;
    }
    return (
      <div>
        <div className={classes.container}>
          {this.state.productList.map((item) => (
            <Product1 item={item} key={item.id} />
          ))}
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    filter: state.filter,
  };
};

export default connect(mapStateToProps)(Products1);
