import { Component } from "react";
import { connect } from "react-redux";
import { useParams } from "react-router-dom";

import classes from "./SingleProduct.module.css";

class SingleProduct1 extends Component {
  constructor(props) {
    super(props);
    this.state = {
      currencyState: [],
      singleItem: [
        {
          name: "Nike Air Huarache Le",
          id: "huarache-x-stussy-le",
          inStock: true,
          gallery: [
            "https://cdn.shopify.com/s/files/1/0087/6193/3920/products/DD1381200_DEOA_2_720x.jpg?v=1612816087",
            "https://cdn.shopify.com/s/files/1/0087/6193/3920/products/DD1381200_DEOA_1_720x.jpg?v=1612816087",
            "https://cdn.shopify.com/s/files/1/0087/6193/3920/products/DD1381200_DEOA_3_720x.jpg?v=1612816087",
            "https://cdn.shopify.com/s/files/1/0087/6193/3920/products/DD1381200_DEOA_5_720x.jpg?v=1612816087",
            "https://cdn.shopify.com/s/files/1/0087/6193/3920/products/DD1381200_DEOA_4_720x.jpg?v=1612816087",
          ],
          description: "<p>Great sneakers for everyday use!</p>",
          category: "clothes",
          attributes: [
            {
              id: "Size",
              name: "Size",
              type: "text",
              items: [
                {
                  displayValue: "40",
                  value: "40",
                  id: "40",
                },
                {
                  displayValue: "41",
                  value: "41",
                  id: "41",
                },
                {
                  displayValue: "42",
                  value: "42",
                  id: "42",
                },
                {
                  displayValue: "43",
                  value: "43",
                  id: "43",
                },
              ],
            },
          ],
          prices: [
            {
              currency: {
                label: "USD",
                symbol: "$",
              },
              amount: 144.69,
            },
            {
              currency: {
                label: "GBP",
                symbol: "£",
              },
              amount: 104,
            },
            {
              currency: {
                label: "AUD",
                symbol: "A$",
              },
              amount: 186.65,
            },
            {
              currency: {
                label: "JPY",
                symbol: "¥",
              },
              amount: 15625.24,
            },
            {
              currency: {
                label: "RUB",
                symbol: "₽",
              },
              amount: 10941.76,
            },
          ],
          brand: "Nike x Stussy",
        },
        {
          name: "Jacket",
          id: "jacket-canada-goosee",
          inStock: true,
          gallery: [
            "https://images.canadagoose.com/image/upload/w_480,c_scale,f_auto,q_auto:best/v1576016105/product-image/2409L_61.jpg",
            "https://images.canadagoose.com/image/upload/w_480,c_scale,f_auto,q_auto:best/v1576016107/product-image/2409L_61_a.jpg",
            "https://images.canadagoose.com/image/upload/w_480,c_scale,f_auto,q_auto:best/v1576016108/product-image/2409L_61_b.jpg",
            "https://images.canadagoose.com/image/upload/w_480,c_scale,f_auto,q_auto:best/v1576016109/product-image/2409L_61_c.jpg",
            "https://images.canadagoose.com/image/upload/w_480,c_scale,f_auto,q_auto:best/v1576016110/product-image/2409L_61_d.jpg",
            "https://images.canadagoose.com/image/upload/w_1333,c_scale,f_auto,q_auto:best/v1634058169/product-image/2409L_61_o.png",
            "https://images.canadagoose.com/image/upload/w_1333,c_scale,f_auto,q_auto:best/v1634058159/product-image/2409L_61_p.png",
          ],
          description: "<p>Awesome winter jacket</p>",
          category: "clothes",
          attributes: [
            {
              id: "Size",
              name: "Size",
              type: "text",
              items: [
                {
                  displayValue: "Small",
                  value: "S",
                  id: "Small",
                },
                {
                  displayValue: "Medium",
                  value: "M",
                  id: "Medium",
                },
                {
                  displayValue: "Large",
                  value: "L",
                  id: "Large",
                },
                {
                  displayValue: "Extra Large",
                  value: "XL",
                  id: "Extra Large",
                },
              ],
            },
          ],
          prices: [
            {
              currency: {
                label: "USD",
                symbol: "$",
              },
              amount: 518.47,
            },
            {
              currency: {
                label: "GBP",
                symbol: "£",
              },
              amount: 372.67,
            },
            {
              currency: {
                label: "AUD",
                symbol: "A$",
              },
              amount: 668.83,
            },
            {
              currency: {
                label: "JPY",
                symbol: "¥",
              },
              amount: 55990.46,
            },
            {
              currency: {
                label: "RUB",
                symbol: "₽",
              },
              amount: 39207.96,
            },
          ],
          brand: "Canada Goose",
        },
      ],
      id: this.props.params,
    };
  }
  itemQuerry = `
  {
    product(id: "${this.props.params.id}"){
      id
      name
      inStock
      description
      category
      brand
      gallery
      attributes{
        id
        name
        type
      }
      prices{
        currency{
          label
          symbol
        }
        amount
      }
    }
  }
  `;

  componentDidMount() {
    fetch("http://localhost:4000/", {
      body: JSON.stringify({ query: this.itemQuerry }),
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
        Dnt: "1",
      },
      method: "POST",
    })
      .then((response) => response.json())
      .then((data) => {
        this.setState({ singleItem: data.data.product });
        console.log(1, this.state.singleItem);
        console.log(2, this.props.params.id);
      });
  }

  //   componentDidUpdate(prevState) {
  //     if (prevState.singleItem !== this.state.singleItem) {
  //       fetch("http://localhost:4000/", {
  //         body: JSON.stringify({ query: this.itemQuerry }),
  //         headers: {
  //           Accept: "application/json",
  //           "Content-Type": "application/json",
  //           Dnt: "1",
  //         },
  //         method: "POST",
  //       })
  //         .then((response) => response.json())
  //         .then((data) => {
  //           this.setState({ singleItem: data.data.product });
  //           console.log(1, this.state.singleItem);
  //           console.log(2, this.props.params.id);
  //         });
  //     }
  //   }

  render() {
    return (
      <div className={classes.container}>
        <div className={classes.left__container}>
          {this.state.singleItem.gallery &&
            this.state.singleItem.gallery.map((item, i) => (
              <img className={classes.left__image} src={item} key={i} />
            ))}
        </div>

        <div className={classes.center__image__container}>
          {this.state.singleItem.gallery && (
            <img
              className={classes.center__image}
              src={this.state.singleItem.gallery[0]}
            />
          )}
        </div>
        <div className={classes.infoContainer}>
          <div className={classes.item__brand}>
            {this.state.singleItem.brand}
          </div>
          <div className={classes.item__name}>{this.state.singleItem.name}</div>
          <div className={classes.item__size}>SIZE:</div>

          <div className={classes.size__buttons}>
            {/* {this.state.singleItem.attributes.size.map((item1) => (
              <button className={classes.btn__size}>{item1}</button>
            ))} */}
          </div>

          <div className={classes.item__priceTxt}>PRICE:</div>

          {/* {this.state.singleItem.prices && (
            <div className={classes.item__price}>
              {
                this.state.singleItem.prices[this.props.currencyState].currency
                  .symbol
              }
              {this.state.singleItem.prices[this.props.currencyState].amount}
            </div>
          )} */}

          {this.state.singleItem.inStock && (
            <button
              className={classes.cart__button}
              onClick={this.props.addToCart}
            >
              ADD TO CART
            </button>
          )}

          {!this.state.singleItem.inStock && (
            <button
              className={`${classes.cart__button} ${
                !this.state.singleItem.inStock && classes.cart__numb
              }`}
            >
              OUT OF STOCK
            </button>
          )}
          <div
            className={classes.description}
            // dangerouslySetInnerHTML={{ __html: this.itemDescription }}
          />
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    currency: state.currency,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    addToCart: (val) => dispatch({ type: "add", obj: val }),
  };
};

function withParams(Component) {
  return (props) => <Component {...props} params={useParams()} />;
}

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(withParams(SingleProduct1));
