import { createStore } from "redux";

const initialState = {
  counter: 0,
  itemsArr: [{ name: "giorgi" }, 2, 3],
  showCounter: true,
};

const counterReducer = (state = initialState, action) => {
  //   if (action.type === "add") {
  //     console.log(1, typeof state.counter);
  //     return {
  //       counter: state.counter + 1,
  //       itemsArr: state.itemsArr.push(action.obj),
  //     };
  //   }
  if (action.type === "add") {
    console.log(state.itemsArr);
    return {
      // returning a copy of orignal state
      ...state, //spreading the original state
      itemsArr: [action.obj, ...state.itemsArr],
      // new todos array
    };
  }

  // case APPEND_TODO:
  //  return { // returning a copy of orignal state
  //   ...state, //copying the original state
  //   todos: [...state.todos, action.payload] //new todos array
  //  }

  if (action.type === "remove") {
    console.log(action.obj);

    return {
      counter: state.counter - 1,
      itemsArr: state.itemsArr.push(action.obj),
    };
  }

  return state;
};

const store = createStore(counterReducer);

export default store;
