import React, { useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import { NavLink, useParams } from "react-router-dom";
import AddToCart from "../assets/AddToCart.png";
import classes from "./Product.module.css";

const Product = ({ item }) => {
  const dispatch = useDispatch();
  const fav = useSelector((state) => state.itemsArr);

  const sendItem = () => {
    // if (!item.quantity) {
    //   item.quantity = 1;
    // } else {
    //   item.quantity++;
    // }
    dispatch({ type: "add", obj: item });
  };

  const sendiTem2 = () => {
    dispatch({ type: "remove", obj: item });
  };

  // const decrementHandler = () => {
  //   dispatch({ type: "decrement" });
  // };

  // const toggleCounterHandler = () => {
  //   dispatch({ type: "toggle" });
  // };

  const [showCart, setShowCart] = useState(false);

  const showFn = () => {
    setShowCart(!showCart);
  };

  const currencyState = useSelector((state) => state.currency);

  return (
    <div className={classes.container}>
      <div
        className={`${classes.column} ${!item.inStock && classes.outOfStock}`}
      >
        <div
          className={classes.card}
          onMouseEnter={showFn}
          onMouseLeave={showFn}
        >
          {showCart && (
            <div className={classes.cart__container} onClick={sendItem}>
              <img className={classes.cart} src={AddToCart} />
            </div>
          )}
          <NavLink to={`/product/${item.id}`}>
            <img
              src={item.gallery[0]}
              className={classes.product__image}
              alt="Image not found"
            />
            <p className={classes.item__name}>
              {item.brand} {item.name}
            </p>
            <p className={classes.item__price}>
              {item.prices[currencyState].currency.symbol}
              {item.prices[currencyState].amount}
            </p>
          </NavLink>
        </div>
      </div>
    </div>
  );
};

export default Product;
