import { Component } from "react";
import { connect } from "react-redux";
import { useParams } from "react-router-dom";

import classes from "./SingleProduct.module.css";

class SingleProduct1 extends Component {
  constructor(props) {
    super(props);
    this.state = {
      currencyState: [],
      singleItem: [],
      id: this.props.params,
      attributes: [],
    };
  }

  itemQuerry = `
  {
    product(id: "${this.props.params.id}"){
      id
      name
      inStock
      description
      category
      brand
      gallery
      attributes {
        id
        name
        type
        items {
          displayValue
          value
          id
        }
      }
      prices{
        currency{
          label
          symbol
        }
        amount
      }
    }
  }
  `;

  componentDidMount() {
    fetch("http://localhost:4000/", {
      body: JSON.stringify({ query: this.itemQuerry }),
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
        Dnt: "1",
      },
      method: "POST",
    })
      .then((response) => response.json())
      .then((data) => {
        this.setState({ singleItem: data.data.product });
      });
  }

  addItem = (val) => {
    let temp = val;
    temp.checked = this.state.attributes;
    this.props.sendItem(temp);
  };

  handleSubmit = (event) => {
    event.preventDefault();

    let dataArr = [];
    // console.log(event.target[0].name);

    for (let index = 0; index < event.target.length; index++) {
      dataArr.push({ [event.target[index].name]: event.target[index].value });
    }

    this.setState({ attributes: dataArr });

    console.log(this.state.attributes);
    // this.addItem(this.props.item)
  };

  render() {
    if (this.state.singleItem.length === 0) {
      return null;
    }
    return (
      <div className={classes.container}>
        <div className={classes.left__container}>
          {this.state.singleItem.gallery.map((item, i) => (
            <img
              className={classes.left__image}
              src={item}
              alt="not found"
              key={i}
            />
          ))}
        </div>

        <div className={classes.center__image__container}>
          {
            <img
              alt="not found"
              className={classes.center__image}
              src={this.state.singleItem.gallery[0]}
            />
          }
        </div>
        <div className={classes.infoContainer}>
          <div className={classes.item__brand}>
            {this.state.singleItem.brand}
          </div>
          <div className={classes.item__name}>{this.state.singleItem.name}</div>

          <form
            className={classes.attributeSelector__container}
            onSubmit={this.handleSubmit}
            style={{
              opacity: !this.state.singleItem.inStock && "0.5",
              pointerEvents: !this.state.singleItem.inStock && "none",
            }}
          >
            {this.state.singleItem.attributes.map((item, i) => (
              <label className={classes.attributeSelector} key={item.id}>
                <p>{item.name}</p>
                <select
                  style={{
                    backgroundColor:
                      item.type === "swatch" ? item.items[2].value : undefined,
                    color:
                      item.type === "swatch" ? item.items[2].value : undefined,
                  }}
                  name={item.name}
                  id={item.id}
                >
                  {item.items.map((e) => (
                    <option
                      style={{
                        backgroundColor: e.value,
                        color: e.value,
                      }}
                      key={e.id}
                    >
                      {e.id}
                    </option>
                  ))}
                </select>
              </label>
            ))}
            {this.state.singleItem.attributes.length > 0 &&
              this.state.singleItem.inStock && (
                <button className={classes.submit__button} type="submit">
                  SUBMIT
                </button>
              )}
          </form>

          <div className={classes.item__priceTxt}>PRICE:</div>
          {
            <div className={classes.item__price}>
              {
                this.state.singleItem.prices[this.props.currency].currency
                  .symbol
              }
              {this.state.singleItem.prices[this.props.currency].amount}
            </div>
          }

          {this.state.singleItem.inStock && this.state.attributes.length > 1 && (
            <button
              className={classes.cart__button}
              onClick={() => this.addItem(this.state.singleItem)}
            >
              ADD TO CART
            </button>
          )}

          {this.state.singleItem.inStock &&
            this.state.attributes.length === 0 && (
              <button
                className={`${classes.cart__button} ${
                  this.state.singleItem.inStock && classes.cart__numb
                }`}
              >
                SUBMIT ATTRIBUTES
              </button>
            )}

          {!this.state.singleItem.inStock && (
            <button
              className={`${classes.cart__button} ${
                !this.state.singleItem.inStock && classes.cart__numb
              }`}
            >
              OUT OF STOCK
            </button>
          )}

          <div
            className={classes.description}
            dangerouslySetInnerHTML={{
              __html: this.state.singleItem.description,
            }}
          />
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    currency: state.currency,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    sendItem: (val) => dispatch({ type: "add", obj: val }),
  };
};

function withParams(Component) {
  return (props) => <Component {...props} params={useParams()} />;
}

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(withParams(SingleProduct1));
