import styled from "styled-components";
import Navbar from "../components/Navbar/Navbar";

const Container = styled.div``;

const Wrapper = styled.div`
  padding: 120px;
  display: flex;
`;

const SmallContainer = styled.div`
  flex: 6%;
`;

const SmlImg = styled.img`
  height: 87px;
  width: 87px;
  margin-bottom: 17px;
`;

const ImgContainer = styled.div`
  flex: 50%;
`;
const InfoContainer = styled.div`
  flex: 34%;
  margin-left: 100px;
`;

const Image = styled.img`
  width: 100%;
  height: 560px;
`;

const Title = styled.div`
  font-family: "Raleway";
  font-style: normal;
  font-weight: 600;
  font-size: 30px;
  line-height: 27px;
  margin-bottom: 20px;
`;

const Smth = styled.div`
  font-family: "Raleway";
  font-style: normal;
  font-weight: 400;
  font-size: 30px;
  line-height: 27px;
  margin-bottom: 50px;
`;

const Size = styled.div`
  font-family: "Roboto Condensed", sans-serif;
  font-style: normal;
  font-weight: 700;
  font-size: 18px;
  line-height: 18px;
  display: flex;
  align-items: center;
  text-align: center;
`;

const Buttons = styled.div`
  background: ${(props) => (props.primary ? "palevioletred" : "white")};
  color: ${(props) => (props.primary ? "white" : "palevioletred")};

  font-size: 1em;
  margin: 1em;
  padding: 0.25em 1em;
  width: 63px;
  height: 45px;
  border: 2px solid palevioletred;
  border-radius: 3px;
`;

const Button = styled.div``;

const Description = styled.div``;

const Price = styled.div``;

const SingleProduct = () => {
  return (
    <Container>
      <Navbar />
      <Wrapper>
        <SmallContainer>
          <SmlImg src="https://fakestoreapi.com/img/81fPKd-2AYL._AC_SL1500_.jpg"></SmlImg>
          <SmlImg src="https://fakestoreapi.com/img/81fPKd-2AYL._AC_SL1500_.jpg"></SmlImg>
          <SmlImg src="https://fakestoreapi.com/img/81fPKd-2AYL._AC_SL1500_.jpg"></SmlImg>
        </SmallContainer>
        <ImgContainer>
          <Image src="https://fakestoreapi.com/img/81fPKd-2AYL._AC_SL1500_.jpg" />
        </ImgContainer>
        <InfoContainer>
          <Title>Apollo</Title>
          <Smth>Running Short</Smth>
          <Size>SIZE:</Size>
          <Buttons>
            <Button>Normal</Button>
          </Buttons>
          <Price>$50</Price>
          <Description>
            Your perfect pack for everyday use and walks in the forest. Stash
            your laptop (up to 15 inches) in the padded sleeve, your everyday
          </Description>
        </InfoContainer>
      </Wrapper>
    </Container>
  );
};

export default SingleProduct;
