import React, { Component } from "react";

import classes from "./Slider.module.css";

class Slider extends Component {
  constructor(props) {
    super(props);
    this.state = {
      current: 0,
      length: props.slides.length,
    };
  }

  nextSlide = () => {
    this.setState({
      current:
        this.state.current === this.state.length - 1
          ? 0
          : this.state.current + 1,
    });
  };

  prevSlide = () => {
    this.setState({
      current:
        this.state.current === 0
          ? this.state.length - 1
          : this.state.current - 1,
    });
  };

  render() {
    if (!Array.isArray(this.props.slides) || this.state.length <= 0) {
      return null;
    }
    return (
      <section className={classes.slider}>
        <div className={classes.left__arrow} onClick={this.prevSlide}>
          {this.props.slides > 0 && `<`}
        </div>
        <div className={classes.right__arrow} onClick={this.nextSlide}>
          {`>`}
        </div>
        {this.props.slides.map((slide, index) => {
          return (
            <div
              className={
                index === this.state.current
                  ? classes.slide.active
                  : classes.slide
              }
              key={index}
            >
              {index === this.state.current && (
                <img src={slide} alt="not found" className={classes.image} />
              )}
            </div>
          );
        })}
      </section>
    );
  }
}

export default Slider;
