import React from "react";
import Navbar from "../components/Navbar/Navbar";
import styled from "styled-components";
import classes from "./Cart.module.css";
import { useSelector, useDispatch } from "react-redux";

const Container = styled.div`
  background-color: #ffffff;
`;

const CartTxt = styled.div`
  font-weight: 700;
  font-size: 32px;
  line-height: 40px;
  margin-left: 120px;
  margin-top: 50px;
  margin-bottom: 50px;
`;

const Wrapper = styled.div`
  margin-right: 100px;
  margin-left: 100px;
  width: 67%;
  padding: 20px;
  border-top: 1px solid #e5e5e5;
  border-bottom: 1px solid #e5e5e5;
`;

const Image = styled.img`
  width: 141px;
  height: 185px;
`;

const Smth = styled.div`
  font-weight: 400;
  font-size: 30px;
  line-height: 27px;
`;

const Quantity = styled.div`
  text-align: center;
  font-weight: 500;
  font-size: 24px;
  line-height: 160%;
  margin-right: 25px;
`;

const Title = styled.div`
  font-weight: 600;
  font-size: 30px;
  line-height: 27px;
`;

const Price = styled.div`
  font-style: normal;
  font-weight: 700;
  font-size: 24px;
  line-height: 18px;
`;

const Buttons = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: space-between;
`;

const Left = styled.div`
  height: 185px;
  float: left;
  display: flex;
  justify-content: flex-start;
  flex-direction: column;
  justify-content: space-between;
`;

const Right = styled.div`
  display: flex;
  justify-content: flex-end;
`;

const SizeBtns = styled.div`
  display: flex;
`;

const EmptyTxt = styled.div`
  font-weight: 700;
  font-size: 32px;
  line-height: 40px;
  display: flex;
  justify-content: center;
  margin-top: 150px;
`;

function Cart() {
  const itemsArr = useSelector((state) => state.itemsArr);
  return (
    <Container>
      <CartTxt>CART</CartTxt>
      {itemsArr.length != 0 &&
        itemsArr.map((item) => (
          <Wrapper>
            <Left>
              <Title>{item.title}</Title>
              <Smth>{item.description.slice(0, 30)}</Smth>
              <Price>{item.price}</Price>
              <SizeBtns>
                <button className={classes.btn__size}>XS</button>
                <button className={classes.btn__size}>S</button>
                <button className={classes.btn__size}>M</button>
                <button className={classes.btn__size}>L</button>
              </SizeBtns>
            </Left>
            <Right>
              <Buttons>
                <button type="submit" className={classes.btn__plus}>
                  +
                </button>
                <Quantity>1</Quantity>
                <button type="submit" className={classes.btn__plus}>
                  -
                </button>
              </Buttons>
              <Image src={item.image} />
            </Right>
          </Wrapper>
        ))}
      {itemsArr.length === 0 && <EmptyTxt>Your cart is empty</EmptyTxt>}
    </Container>
  );
}
export default Cart;
