import styled from "styled-components";
import styles from "./SingleProduct.module.css";
import { useParams } from "react-router-dom";
import { useState, useEffect } from "react";

const Container = styled.div``;

const Wrapper = styled.div`
  padding: 120px;
  display: flex;
`;

const SmallContainer = styled.div`
  flex: 10%;
  flex-direction: column;
`;

const SmlImg = styled.img`
  height: 87px;
  width: 87px;
  margin-bottom: 17px;
  display: flex;
  flex-direction: column;
`;

const ImgContainer = styled.div`
  flex: 50%;
`;
const InfoContainer = styled.div`
  flex: 30%;
  padding-left: 8%;
  padding-right: 8%;
  word-wrap: break-word;
`;

const Image = styled.img`
  width: 100%;
  height: 560px;
`;

const Title = styled.div`
  font-family: "Raleway";
  font-style: normal;
  font-weight: 600;
  font-size: 30px;
  line-height: 27px;
  margin-bottom: 20px;
`;

const Smth = styled.div`
  font-family: "Raleway";
  font-style: normal;
  font-weight: 400;
  font-size: 30px;
  line-height: 27px;
  margin-bottom: 35px;
`;

const Size = styled.div`
  font-family: "Roboto Condensed", sans-serif;
  font-style: normal;
  font-weight: 700;
  font-size: 18px;
  line-height: 18px;
  display: flex;
  align-items: center;
  text-align: center;
  margin-bottom: 5px;
`;

const Button = styled.div`
  margin-bottom: 35px;
  display: flex;
  justify-content: space-between;
`;

const Description = styled.div`
  font-family: "Roboto";
  font-style: normal;
  font-weight: 400;
  font-size: 16px;
  line-height: 159.96%;
`;

const Price = styled.div`
  font-family: "Roboto Condensed";
  font-style: normal;
  font-weight: 700;
  font-size: 18px;
  line-height: 18px;
  margin-bottom: 20px;
`;

const PriceD = styled.div`
  font-family: "Raleway";
  font-style: normal;
  font-weight: 700;
  font-size: 24px;
  line-height: 18px;
  margin-bottom: 30px;
`;

const SingleProduct = () => {
  let { id } = useParams();
  const itemQuerry = `
{
  product(id: "${id}"){
    id
    name
    inStock
    description
    category
    brand
    gallery
    attributes{
      id
      name
      type
    }
    prices{
      currency{
        label
        symbol
      }
      amount
    }
  }
}
`;

  const [singleItem, setsingleItem] = useState([]);
  useEffect(() => {
    fetch("http://localhost:4000/", {
      body: JSON.stringify({ query: itemQuerry }),
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
        Dnt: "1",
      },
      method: "POST",
    })
      .then((response) => response.json())
      .then((data) => {
        setsingleItem(data.data.product);
      });
  }, []);

  if (singleItem.length === 0) {
    return null;
  }
  return (
    <div className={styles.container}>
      <div className={styles.wrapper}>
        <div className={styles.smallContainer}>
          <img className={styles.smlImg} src={singleItem.gallery[0]}></img>
          <img className={styles.smlImg} src={singleItem.gallery[0]}></img>
          <img className={styles.smlImg} src={singleItem.gallery[0]}></img>
        </div>
        <div className={styles.imgContainer}>
          <img className={styles.image} src={singleItem.gallery[0]} />
        </div>
        <div className={styles.infoContainer}>
          <div className={styles.title}>{singleItem.category}</div>
          <div className={styles.smth}>{singleItem.name}</div>
          <div className={styles.size}>SIZE:</div>

          <div className={styles.button}>
            <button className={styles.btn__size}>XS</button>
            <button className={styles.btn__size}>S</button>
            <button className={styles.btn__size}>M</button>
            <button className={styles.btn__size}>L</button>
          </div>

          <div className={styles.price}>PRICE:</div>
          <div className={styles.priceD}>{singleItem.prices[0].amount}$</div>
          <button className={styles.cart__button}>ADD TO CART</button>
          <div className={styles.description}>{singleItem.description}</div>
        </div>
      </div>
    </div>
  );
};

export default SingleProduct;
