import React, { Component, useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import { NavLink, useParams } from "react-router-dom";
import AddToCart from "../assets/AddToCart.png";
import classes from "./Product.module.css";

class Product extends Component {
  constructor() {
    super();
    this.state = { showCart: false };
  }

  showFn = () => {
    this.setState((curState) => {
      return { showCart: !curState.showCart };
    });
  };

  render() {
    return (
      <div className={classes.container}>
        <div className={`${classes.column} ${classes.outOfStock} `}>
          <div
            className={classes.card}
            onMouseEnter={this.showFn.bind(this)}
            onMouseLeave={this.showFn.bind(this)}
          >
            {this.state.showCart && (
              <div className={classes.cart__container}>
                <img className={classes.cart} src={AddToCart} />
              </div>
            )}
            <NavLink to={`/product/${this.item.id}`}>
              {!this.item.inStock && (
                <p className={classes.outOfStockText}>OUT OF STOCK</p>
              )}
              <img
                src={this.item.gallery[0]}
                className={classes.product__image}
                alt="Image not found"
              />
              <p className={classes.this.item__name}>
                {this.item.brand} {this.item.name}
              </p>
              <p className={classes.this.item__price}>
                {this.item.prices.currency.symbol}
                {this.item.prices.amount}
              </p>
            </NavLink>
          </div>
        </div>
      </div>
    );
  }
}

export default Product;
