import React, { Component, useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import { NavLink, useParams } from "react-router-dom";
import AddToCart from "../assets/AddToCart.png";
import classes from "./Product.module.css";

class Product extends Component {
  constructor() {
    this.state = { showCart: false };
  }

  showFn = () => {
    this.setState((curState) => {
      return { showCart: !curState.showCart };
    });
  };

  render() {
    return (
      <div className={classes.container}>
        <div
          className={`${classes.column} ${
            !item.inStock && classes.outOfStock
          } `}
        >
          <div
            className={classes.card}
            onMouseEnter={this.showFn.bind(this)}
            onMouseLeave={showFn}
          >
            {this.state.showCart && item.inStock && (
              <div className={classes.cart__container} onClick={sendItem}>
                <img className={classes.cart} src={AddToCart} />
              </div>
            )}
            <NavLink to={`/product/${item.id}`}>
              {!item.inStock && (
                <p className={classes.outOfStockText}>OUT OF STOCK</p>
              )}
              <img
                src={item.gallery[0]}
                className={classes.product__image}
                alt="Image not found"
              />
              <p className={classes.item__name}>
                {item.brand} {item.name}
              </p>
              <p className={classes.item__price}>
                {item.prices[currencyState].currency.symbol}
                {item.prices[currencyState].amount}
              </p>
            </NavLink>
          </div>
        </div>
      </div>
    );
  }
}

export default Product;
