import React from "react";
import { Component } from "react";
import logo from "../../assets/logo.png";
import styled from "styled-components";
import sign from "../../assets/$.png";
import Cart from "../../assets/Cart.png";
import Vector from "../../assets/Vector.png";
import classes from "./Navbar.module.css";
import CartDropdown from "../Dropdowns/CartDropdown";
import CurrencySwitcher from "../Dropdowns/CurrencySwitcher";
import { NavLink } from "react-router-dom";
import { useSelector, useDispatch } from "react-redux";

const Container = styled.div`
  height: 80px;
  padding-left: 100px;
  padding-right: 100px;
`;

const Wrapper = styled.div`
  padding: 10px 20px;
  display: flex;
  justify-content: space-between;
  align-items: center;
`;

const Left = styled.div`
  flex: 1;
  display: flex;
  font-weight: 600;
  font-size: 16px;
`;
const Center = styled.div`
  flex: 1;
  text-align: center;
  padding-top: 10px;
`;

const Right = styled.div`
  flex: 1;
  justify-content: flex-end;
  display: flex;
  align-items: center;
`;

const Lmenu = styled.div`
  align-items: center;
  margin-left: 40px;
`;

const Rmenu = styled.div`
  align-items: center;
  margin-right: 40px;
`;

const Navbar = () => {
  const dispatch = useDispatch();

  const filter = useSelector((state) => state.filter);

  const setFilterState = (val) => {
    dispatch({ type: "set", filterState: val });
  };

  // const sendiTem2 = () => {
  //   dispatch({ type: "remove", obj: item });
  // };

  return (
    <div className={classes.container}>
      <div className={classes.wrapper}>
        <div className={classes.left__menu}>
          <a
            onClick={() => setFilterState(0)}
            className={filter === 0 ? classes.active : undefined}
          >
            ALL
          </a>
          <div className={classes.lmenu__categories}>
            <a
              onClick={() => setFilterState(1)}
              className={filter === 1 ? classes.active : undefined}
            >
              CLOTHES
            </a>
          </div>
          <div className={classes.lmenu__categories}>
            <a
              onClick={() => setFilterState(2)}
              className={filter === 2 ? classes.active : undefined}
            >
              TECH
            </a>
          </div>
        </div>
        <div className={classes.center__menu}>
          <NavLink to="">
            <img src={logo} />
          </NavLink>
        </div>
        <div className={classes.right__menu}>
          <div className={classes.rmenu__}>
            <div className={classes.dropdown}>
              <button className={classes.dropbtn}>
                <img src={sign} />
                <img className={classes.rotate} src={Vector} />
              </button>
              <div className={classes.dropdown_content}>
                <CurrencySwitcher />
              </div>
            </div>
          </div>
          <div className={classes.rmenu}>
            <div className={classes.dropdown}>
              <button className={classes.dropbtn}>
                <img src={Cart} />
              </button>
              <div className={classes.dropdown_content}>
                <CartDropdown />
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Navbar;
