import React from "react";
import styled from "styled-components";
import { arr } from "../Data";
import Product from "./Product";
import { useEffect } from "react";

const Container = styled.div`
  display: flex;
  flex-wrap: wrap;
  justify-content: space-between;
`;

const productsQuerry = `
{
  categories {
    name
    products {
      name
      id
      inStock
      gallery
      description
      category
      attributes {
        id
        name
        type
      }
      prices {
        currency {
          label
        }
        amount
      }
      brand
    }
  }
}
`;

const Products = () => {
  useEffect(() => {
    fetch("http://localhost:4000/", {
      method: "Get",
      headers: {},
      body: JSON.stringify({ query: productsQuerry }),
    }).then(response);
  }, []);

  return (
    <div>
      <Container>
        {arr.map((item) => (
          <Product item={item} key={item.id} />
        ))}
      </Container>
    </div>
  );
};

export default Products;
